-- Table structure for edu_course_description
-- ----------------------------
DROP TABLE IF EXISTS `edu_course_description`;
CREATE TABLE `edu_course_description`  (
  `id` char(19) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '课程ID',
  `description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '课程简介',
  `gmt_create` datetime(0) NOT NULL COMMENT '创建时间',
  `gmt_modified` datetime(0) NOT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '课程简介' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of edu_course_description
-- ----------------------------
INSERT INTO `edu_course_description` VALUES ('1327610045098954754', '<p>无论是日常生活、娱乐、科研、教育、医疗，还是生产、交通，甚至是战争、太空探索，我们使用的装备，都离不开半导体技术。目前最先进的芯片上，集成了数十亿个元器件，内部结构、精细化程度，让人叹为观止，其晶体管栅长仅相当于头发丝直径的五千亿分之一。目前集成电路正在向吉规模进军，人类正在向量子效应发出挑战。你想了解它们的原理吗？你想学会如何利用集成元器件进行电子设计和自动控制吗？</p>\n<p>《数字电子技术》课程是电子技术方面入门性质的技术基础课，将由黄黎红、林振衡等老师带领大家学习数字电子技术方面的基本知识、基本理论和基本技能，为深入学习数字电子技术及其在专业中的应用打好基础。</p>\n<p>本课程既具有很强的理论性、系统性，又有很强的工程性、实践性，课程整体设计体现以应用为目的，按照&ldquo;必须&rdquo;、&ldquo;够用&rdquo;的原则组织教学，并加强知识的实用性，突出对本领域中&ldquo;新器件、新技术、新应用&rdquo;的介绍，做到&ldquo;讲、学、做&rdquo;统一协调，其主要内容有：绪论、逻辑代数基础、集成逻辑门电路、组合逻辑电路、集成触发器、时序逻辑电路、脉冲的产生与整形等；这些内容几乎涵盖了数字电子技术的所有基础理论，可为学习者着手仿真及设计中大规模数字电路、数字电子系统提供丰富的专业知识。</p>', '2020-11-14 21:51:15', '2020-11-14 21:51:15');
INSERT INTO `edu_course_description` VALUES ('1327626186626240513', '<p>大学计算机;是面向各专业本科生开设的计算机系列课程中第一层次的课程，是本科生必修的通识教育课程，更是二十一世纪人人都需要掌握的一种文化。本课程以培养思维能力为核心，以计算机文化素养培养、激发学生主动学习意识为主导思想，实现计算机赋能教育。</p> 教学目标是通过学习，掌握信息化社会数据处理的方法与手段。课程基于&ldquo;计算文化与计算基础&mdash;计算平台&mdash;数据处理与管理&mdash;问题求解与实现&rdquo;这样一条脉络，介绍计算机科学与技术所蕴含的计算思想与计算技术；强化学生对计算机科学的认知，使之掌握大数据时代数据处理与问题求解的基本方法与手段、建立正确的科学思维模式，并初步具备灵活运用程序设计的思想与方法求解实际问题的能力。</p><p>; 通过本课程的学习，将培养学生学习与思维能力，实现赋能教育，倡导;人人都要学会像计算机科学家一样思考与解决问题,灵活地运用计算技术解决问题。</p>\n; 整个课程共八讲，每周1讲，时长10周，通过学习来了解并掌握从计算平台到问题求解的全过程，为后续计算机程序设计等课程的深入学习奠定良好的基础。在授课过程中，我们会通过实验案例辅助知识学习与应用，在案例的不断深化和提升中，由浅入深地引导大家循序渐进地学习与练习，使之达到对问题求解的融会贯通，不断提升思维能力。</p>', '2020-11-14 22:55:24', '2020-11-14 22:55:24');
INSERT INTO `edu_course_description` VALUES ('1332890172651622402', '', '2020-11-29 11:32:36', '2020-11-29 11:32:36');
INSERT INTO `edu_course_description` VALUES ('1333060849530740737', '<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;国防科学技术大学前身是1953年创建的&ldquo;哈军工&rdquo;，1970年学校主体南迁长沙，1978年改建为国防科学技术大学。是国防部和教育部双重领导下的国家重点综合性大学，列入国家&ldquo;985工程&rdquo;和&ldquo;211工程&rdquo;的重点建设。<br />&nbsp; &nbsp; &nbsp;《大学英语（口语）》先修课特别适应学有余力的高中生，以话题为内容，以口语技能为牵引，从纵深两个维度上提高学员对英语口语的实际应用能力，修完该门课程，可以更好地适应大学阶段的英语学习，是高中和大学的有力衔接。<br />&nbsp; &nbsp; &nbsp; 本视频课程共20单元，分20课时讲授，内容广泛涵盖了出国学习和生活中所涉及的主要话题。课程引入雅思考试对口语能力的界定标准和培养方法，在基本日常对话、独立长段输出、多人讨论辩论三个层次上逐步进阶性培养英语口语能力，既为今后有留学计划的学员做应试准备，又全面提高学员日常英语口语输出能力。<br />&nbsp; &nbsp; &nbsp; 在本课程结束时，学员能够具备进行国际交流时需要的基本口语能力：能就休闲类话题进行日常对话，并自然使用交际策略；能就一般话题进行持续3分钟左右的即兴演说，并做到组织条理清晰，连接自然，细节充分；能够参与抽象类话题的讨论与辩论，做到分析复杂情况，在此基础上发表并支持自己的观点，并驳斥反观点。</p>\n<p>语言的学习是一种需要高频次接触，反复练习的学习过程，仅仅靠每周的视频学习你可能无法真正充分掌握和记忆。所以在MOOC之外，我们还推出了一门<a href=\"https://www.icourse163.org/columns/1003605003.htm?speaking\" target=\"_blank\" rel=\"noopener\">《海外生存口语全攻略》</a>的名师专栏，80个真实交流场景，1000个地道口头表达，带你进入英语口语移动课堂。</p>', '2020-11-29 22:50:49', '2020-11-29 22:50:49');
INSERT INTO `edu_course_description` VALUES ('1333066930222964737', '<p>计算机是运算工具，更是创新平台，高效有趣地利用计算机需要更简洁实用的编程语言。Python简洁却强大、简单却专业，它是当今世界最受欢迎的编程语言，学好它终身受用。请跟随我们，学习并掌握Python语言，一起动起来，站在风口、享受创新！</p>\n<p>&nbsp;</p>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">授课目标</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>本课程适合如下教学目标：</p>\n<ul class=\" list-paddingleft-2\">\n<li>\n<p><strong>程序设计入门课</strong>：面向各层次各专业大学在校生、部分优秀高中生，作为程序设计入门课程</p>\n</li>\n<li>\n<p><strong>体系化编程基础</strong>：面向拟构建坚实编程能力的自学者，作为不断奋斗的参考在线课程</p>\n</li>\n<li>\n<p><strong>Python科目备考</strong>：面向全国计算机等级考试二级Python科目的备考考生，作为在线备考资源</p>\n</li>\n<li>\n<p><strong>再试一次的尝试</strong>：面向拟放弃计算机或编程学习的学习者，作为再试一次的课程资源，学不会这门课学再放弃不迟...</p>\n</li>\n</ul>\n<p>&nbsp;</p>\n</div>\n</div>', '2020-11-29 23:14:58', '2020-11-29 23:14:58');
INSERT INTO `edu_course_description` VALUES ('1333069230798708737', '<div class=\"category-title f-f0\"><span class=\"f-ib f-vam\">课程概述</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>&nbsp; 高等数学以微积分为主要内容。微积分是研究运动和变化的数学，它广泛应用于自然科学、社会科学、经济管理、工程技术等各个领域，其内容、思想与方法对培养各类人才全面综合素质具有不可替代的作用。高等数学课程着重培养学员的抽象思维能力、逻辑推理能力、空间想象能力、实验及观察能力以及综合运用所学知识分析问题解决问题的能力，也是开展数学素质教育、培养学习者创新精神和创新能力的重要课程。<br /><br />&nbsp; 为符合MOOC课程的特点并方便广大学习者，我们将传统意义的高等数学课程分成五个部分，共100讲，由十五章组成。主要内容包括：极限与连续、数值级数、一元函数导数与积分、常微分方程、空间解析几何、多元函数微分学及应用、重积分、曲线与曲面积分、幂级数与傅里叶级数。<br />&nbsp;&nbsp;</p>\n<p>&nbsp; 高等数学（二）共26讲，主要内容有：一元函数导数及其应用、一元函数积分及其应用。</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">课程大纲</span></div>\n<div id=\"j-course-outline\" class=\"category-content j-cover-overflow\">\n<div class=\"outline\">\n<div class=\"f-richEditorText\">\n<p>高等数学二（共26讲）</p>\n<p>第一讲 导数概念</p>\n<p>1、问题引入</p>\n<p>2、问题求解</p>\n<p>3、导数的定义及几何意义</p>\n<p>4、导数存在的条件</p>\n<p>5、导函数</p>\n<p>第二讲 导数运算法则</p>\n<p>1、问题引入</p>\n<p>2.1、求导法则&mdash;&mdash;四则运算法则</p>\n<p>2.2、求导法则&mdash;&mdash;反函数与复合函数求导法则</p>\n<p>3、基本初等函数求导公式</p>\n<p>4、导数综合计算</p>\n<p>第三讲 高阶导数</p>\n<p>1、问题引入</p>\n<p>2、高阶导数</p>\n<p>3、隐函数的导数</p>\n<p>4、参数方程确定函数的导数</p>\n<p>第四讲 局部线性化与微分</p>\n<p>1、问题引入</p>\n<p>2、微分的概念</p>\n<p>3、微分在近似计算中的应用</p>\n<p>4、一阶微分形式的不变性</p>\n<p>5、高阶微分</p>\n<p>第五讲 导数在实际问题中的应用</p>\n<p>1、问题引入</p>\n<p>2、变化率</p>\n<p>3、相关变化率</p>\n<p>第六讲 不定积分的概念与性质</p>\n<p>1、问题引入</p>\n<p>2、原函数</p>\n<p>3、不定积分的概念与性质</p>\n<p>4、不定积分基本公式</p>\n<p>5、不定积分的简单应用</p>\n<p>第七讲 函数的极值及最优化应用</p>\n<p>1、问题引入</p>\n<p>2、极值的概念</p>\n<p>3、可微函数极值的必要条件</p>\n<p>4、极值判定的一个充分条件</p>\n<p>5、求最大值与最小值</p>\n<p>第八讲 罗尔定理与拉格朗日中值定理</p>\n<p>1、问题引入</p>\n<p>2、罗尔定理</p>\n<p>3、拉格朗日中值定理</p>\n<p>4、微分中值定理应用</p>\n<p>第九讲 柯西中值定理与洛必达法则</p>\n<p>1、问题引入</p>\n<p>2、柯西中值定理</p>\n<p>3.1、洛必达法则&mdash;&mdash;法则的几种情形</p>\n<p>3.2、洛必达法则&mdash;&mdash;不定型极限的计算</p>\n<p>第十讲 函数的多项式逼近</p>\n<p>1、问题引入</p>\n<p>2、函数的多项式逼近</p>\n<p>3、几个初等函数的麦克劳林多项式</p>\n<p>4、逼近效果的图形演示</p>\n<p>第十一讲 泰勒公式</p>\n<p>1、问题引入</p>\n<p>2、误差估计及泰勒公式</p>\n<p>3、几个初等函数的麦克劳林公式</p>\n<p>4、间接法求泰勒公式</p>\n<p>第十二讲 泰勒公式的应用</p>\n<p>1、问题引入</p>\n<p>2、近似计算</p>\n<p>3、极限计算</p>\n<p>4、问题证明</p>\n<p>第十三讲 函数的单调性与凹凸性</p>\n<p>1、问题引入</p>\n<p>2.1、函数的单调性判定&mdash;&mdash;单调性判定方法</p>\n<p>2.2、函数的单调性判定&mdash;&mdash;极值第一充分条件</p>\n<p>2.3、函数的单调性判定&mdash;&mdash;极值第二充分条件</p>\n<p>3.1、函数凹凸性及其判定&mdash;&mdash;凸函数的概念</p>\n<p>3.2、函数凹凸性及其判定&mdash;&mdash;函数凸性判别方法</p>\n<p>第十四讲 利用导数研究函数的几何性态</p>\n<p>1、问题引入</p>\n<p>2、函数图形的几何性态回顾</p>\n<p>3、函数图形的渐近线</p>\n<p>4、函数的几何性态研究</p>\n<p>第十五讲 曲率</p>\n<p>1、问题引入</p>\n<p>2、弧微分</p>\n<p>3.1、曲率的概念及计算&mdash;&mdash;曲率的定义</p>\n<p>3.2、曲率的概念及计算&mdash;&mdash;曲率的计算</p>\n<p>4、曲率半径与曲率圆</p>\n<p>第十六讲 解非线性方程的牛顿切线法</p>\n<p>1、问题引入</p>\n<p>2.1、牛顿法思想及迭代公式&mdash;&mdash;简单迭代法</p>\n<p>2.2、牛顿法思想及迭代公式&mdash;&mdash;牛顿迭代法</p>\n<p>3、牛顿法的收敛性</p>\n<p>第十七讲 定积分的概念</p>\n<p>1、问题引入</p>\n<p>2、几个典型的定积分问题</p>\n<p>3、定积分的定义</p>\n<p>4、定积分的几何意义</p>\n<p>5、定积分的基本性质</p>\n<p>第十八讲 定积分的性质</p>\n<p>1、问题引入</p>\n<p>2、函数的可积性</p>\n<p>3、定积分求特殊和式的极限</p>\n<p>4、积分中值定理</p>\n<p>第十九讲 微积分基本公式</p>\n<p>1、问题引入</p>\n<p>2、微积分基本公式</p>\n<p>3、变限积分函数</p>\n<p>4、原函数的存在性</p>\n<p>5、变限积分的综合应用</p>\n<p>第二十讲 积分的变量替换法</p>\n<p>1、问题引入</p>\n<p>2、不定积分的第一类换元法</p>\n<p>3、不定积分的第二类换元法</p>\n<p>4、定积分的换元法</p>\n<p>第二十一讲 积分的分部积分法</p>\n<p>1、问题引入</p>\n<p>2.1、不定积分的分部积分法&mdash;&mdash;基本计算</p>\n<p>2.2、不定积分的分部积分法&mdash;&mdash;递推公式</p>\n<p>3.1、定积分的分部积分法&mdash;&mdash;基本计算</p>\n<p>3.2、定积分的分部积分法&mdash;&mdash;华莱士公式</p>\n<p>第二十二讲 积分计算综合</p>\n<p>1、问题引入</p>\n<p>2、几类积分计算总结</p>\n<p>3、奇偶函数的定积分</p>\n<p>4、周期函数的定积分</p>\n<p>第二十三讲 定积分的几何应用</p>\n<p>1、问题引入</p>\n<p>2.1、平面图形的面积&mdash;&mdash;面积的积分表示</p>\n<p>2.2、平面图形的面积&mdash;&mdash;面积的计算</p>\n<p>3.1、体积&mdash;&mdash;已知截面面积立体的体积</p>\n<p>3.2、体积&mdash;&mdash;已知截面面积立体的体积</p>\n<p>第二十四讲 定积分的物理应用</p>\n<p>1、、问题引入</p>\n<p>2、功</p>\n<p>3、静压力</p>\n<p>4、引力</p>\n<p>第二十五讲 反常积分讲</p>\n<p>1、问题引入</p>\n<p>2、无穷区间的反常积分</p>\n<p>3、无界函数的反常积分</p>\n<p>4、反常积分的敛散性</p>\n<p>第二十六讲 定积分的数值计算</p>\n<p>1、问题引入</p>\n<p>2、数值积分的基本思想</p>\n<p>3、矩形公式</p>\n<p>4、梯形公式</p>\n<p>5、辛普森公式</p>\n</div>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">预备知识</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>高等数学（一）</p>\n</div>\n</div>\n<div id=\"requirements\" class=\"category-title f-f0 certRope\">&nbsp;<span class=\"f-ib f-vam\">证书要求</span></div>\n<div class=\"category-content j-cover-overflow certRope\">\n<div class=\"f-richEditorText\">\n<p>本课程采用百分制计分，包括单元测验、讨论和期末考试。其中：</p>\n<p>单元测验为客观题，占总成绩的30%；</p>\n<p>讨论部分占总成绩的10%，在&ldquo;课堂讨论区&rdquo;回复10个及以上的帖子，可以得到讨论部分的满分；</p>\n<p>期末考试为客观题，占总成绩的60%。</p>\n<p>总成绩：&ldquo;60&le;得分&lt;85&rdquo;的学员可申请合格证书，&ldquo;85&le;得分&rdquo;的学员可申请优秀学员证书。</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">参考资料</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>【1】朱健民，李建平. 高等数学（第二版）（上、下）.北京：高等教育出版社，2015年.</p>\n<p>&nbsp;</p>\n<p><img id=\"1\" src=\"https://nos.netease.com/edu-image/54918672036603F02F2DDE9C288500E3.jpg?imageView&amp;thumbnail=520x520&amp;quality=100\" /></p>\n<p>上册购买链接：<a href=\"https://www.hepmall.com/index.php/product-16589.html\" target=\"_blank\" rel=\"noopener\">https://www.hepmall.com/index.php/product-16589.html</a></p>\n<p>&nbsp;</p>\n<p>下册购买链接：<a href=\"https://www.hepmall.com/index.php/product-16799.html\" target=\"_blank\" rel=\"noopener\">https://www.hepmall.com/index.php/product-16799.html</a></p>\n<p>&nbsp;</p>\n<p>【2】李建平，朱健民. 高等数学的典型例题与解法（上、下）.长沙：国防科技大学出版社，2003年.</p>\n<p>【3】李建平，朱健民等. 高等数学课程实验.北京：科学出版社，2011年.</p>\n<p>【4】李建平，朱健民等. 高等数学试题汇编.长沙：国防科技大学出版社，2013年.</p>\n<p>&nbsp;</p>\n</div>\n</div>', '2020-11-29 23:24:07', '2020-11-29 23:24:07');
INSERT INTO `edu_course_description` VALUES ('1333070303596814337', '<div id=\"j-course-heading-intro\" class=\"course-heading-intro\">\n<div id=\"j-rectxt2\" class=\"course-heading-intro_intro\">正是因为数学的抽象性，人们对数学望而生畏，但也正是数学这一特性，使人们在繁杂的世界中，逐步懂得宇宙发展的奥秘。为满足广大学习者学习高等数学的需求，全国优秀教师、国防科技大学朱健民教授，将在高等数学MOOC视频课堂，用形象生动的语言解释微积分思想形成的过程，与你一道感受数学的无穷魅力！</div>\n<div class=\"course-heading-intro_team\">&mdash;&mdash; 课程团队</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">课程概述</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>&nbsp; 高等数学以微积分为主要内容。微积分是研究运动和变化的数学，它广泛应用于自然科学、社会科学、经济管理、工程技术等各个领域，其内容、思想与方法对培养各类人才全面综合素质具有不可替代的作用。高等数学课程着重培养学员的抽象思维能力、逻辑推理能力、空间想象能力、实验及观察能力以及综合运用所学知识分析问题解决问题的能力，也是开展数学素质教育、培养学习者创新精神和创新能力的重要课程。<br /><br />&nbsp; 为符合MOOC课程的特点并方便广大学习者，我们将传统意义的高等数学课程分成五个部分，共100讲，由十五章组成。主要内容包括：极限与连续、数值级数、一元函数导数与积分、常微分方程、空间解析几何、多元函数微分学及应用、重积分、曲线与曲面积分、幂级数与傅里叶级数。<br />&nbsp;&nbsp;</p>\n<p>&nbsp; 高等数学（二）共26讲，主要内容有：一元函数导数及其应用、一元函数积分及其应用。</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">课程大纲</span></div>\n<div id=\"j-course-outline\" class=\"category-content j-cover-overflow\">\n<div class=\"outline\">\n<div class=\"f-richEditorText\">\n<p>高等数学二（共26讲）</p>\n<p>第一讲 导数概念</p>\n<p>1、问题引入</p>\n<p>2、问题求解</p>\n<p>3、导数的定义及几何意义</p>\n<p>4、导数存在的条件</p>\n<p>5、导函数</p>\n<p>第二讲 导数运算法则</p>\n<p>1、问题引入</p>\n<p>2.1、求导法则&mdash;&mdash;四则运算法则</p>\n<p>2.2、求导法则&mdash;&mdash;反函数与复合函数求导法则</p>\n<p>3、基本初等函数求导公式</p>\n<p>4、导数综合计算</p>\n<p>第三讲 高阶导数</p>\n<p>1、问题引入</p>\n<p>2、高阶导数</p>\n<p>3、隐函数的导数</p>\n<p>4、参数方程确定函数的导数</p>\n<p>第四讲 局部线性化与微分</p>\n<p>1、问题引入</p>\n<p>2、微分的概念</p>\n<p>3、微分在近似计算中的应用</p>\n<p>4、一阶微分形式的不变性</p>\n<p>5、高阶微分</p>\n<p>第五讲 导数在实际问题中的应用</p>\n<p>1、问题引入</p>\n<p>2、变化率</p>\n<p>3、相关变化率</p>\n<p>第六讲 不定积分的概念与性质</p>\n<p>1、问题引入</p>\n<p>2、原函数</p>\n<p>3、不定积分的概念与性质</p>\n<p>4、不定积分基本公式</p>\n<p>5、不定积分的简单应用</p>\n<p>第七讲 函数的极值及最优化应用</p>\n<p>1、问题引入</p>\n<p>2、极值的概念</p>\n<p>3、可微函数极值的必要条件</p>\n<p>4、极值判定的一个充分条件</p>\n<p>5、求最大值与最小值</p>\n<p>第八讲 罗尔定理与拉格朗日中值定理</p>\n<p>1、问题引入</p>\n<p>2、罗尔定理</p>\n<p>3、拉格朗日中值定理</p>\n<p>4、微分中值定理应用</p>\n<p>第九讲 柯西中值定理与洛必达法则</p>\n<p>1、问题引入</p>\n<p>2、柯西中值定理</p>\n<p>3.1、洛必达法则&mdash;&mdash;法则的几种情形</p>\n<p>3.2、洛必达法则&mdash;&mdash;不定型极限的计算</p>\n<p>第十讲 函数的多项式逼近</p>\n<p>1、问题引入</p>\n<p>2、函数的多项式逼近</p>\n<p>3、几个初等函数的麦克劳林多项式</p>\n<p>4、逼近效果的图形演示</p>\n<p>第十一讲 泰勒公式</p>\n<p>1、问题引入</p>\n<p>2、误差估计及泰勒公式</p>\n<p>3、几个初等函数的麦克劳林公式</p>\n<p>4、间接法求泰勒公式</p>\n<p>第十二讲 泰勒公式的应用</p>\n<p>1、问题引入</p>\n<p>2、近似计算</p>\n<p>3、极限计算</p>\n<p>4、问题证明</p>\n<p>第十三讲 函数的单调性与凹凸性</p>\n<p>1、问题引入</p>\n<p>2.1、函数的单调性判定&mdash;&mdash;单调性判定方法</p>\n<p>2.2、函数的单调性判定&mdash;&mdash;极值第一充分条件</p>\n<p>2.3、函数的单调性判定&mdash;&mdash;极值第二充分条件</p>\n<p>3.1、函数凹凸性及其判定&mdash;&mdash;凸函数的概念</p>\n<p>3.2、函数凹凸性及其判定&mdash;&mdash;函数凸性判别方法</p>\n<p>第十四讲 利用导数研究函数的几何性态</p>\n<p>1、问题引入</p>\n<p>2、函数图形的几何性态回顾</p>\n<p>3、函数图形的渐近线</p>\n<p>4、函数的几何性态研究</p>\n<p>第十五讲 曲率</p>\n<p>1、问题引入</p>\n<p>2、弧微分</p>\n<p>3.1、曲率的概念及计算&mdash;&mdash;曲率的定义</p>\n<p>3.2、曲率的概念及计算&mdash;&mdash;曲率的计算</p>\n<p>4、曲率半径与曲率圆</p>\n<p>第十六讲 解非线性方程的牛顿切线法</p>\n<p>1、问题引入</p>\n<p>2.1、牛顿法思想及迭代公式&mdash;&mdash;简单迭代法</p>\n<p>2.2、牛顿法思想及迭代公式&mdash;&mdash;牛顿迭代法</p>\n<p>3、牛顿法的收敛性</p>\n<p>第十七讲 定积分的概念</p>\n<p>1、问题引入</p>\n<p>2、几个典型的定积分问题</p>\n<p>3、定积分的定义</p>\n<p>4、定积分的几何意义</p>\n<p>5、定积分的基本性质</p>\n<p>第十八讲 定积分的性质</p>\n<p>1、问题引入</p>\n<p>2、函数的可积性</p>\n<p>3、定积分求特殊和式的极限</p>\n<p>4、积分中值定理</p>\n<p>第十九讲 微积分基本公式</p>\n<p>1、问题引入</p>\n<p>2、微积分基本公式</p>\n<p>3、变限积分函数</p>\n<p>4、原函数的存在性</p>\n<p>5、变限积分的综合应用</p>\n<p>第二十讲 积分的变量替换法</p>\n<p>1、问题引入</p>\n<p>2、不定积分的第一类换元法</p>\n<p>3、不定积分的第二类换元法</p>\n<p>4、定积分的换元法</p>\n<p>第二十一讲 积分的分部积分法</p>\n<p>1、问题引入</p>\n<p>2.1、不定积分的分部积分法&mdash;&mdash;基本计算</p>\n<p>2.2、不定积分的分部积分法&mdash;&mdash;递推公式</p>\n<p>3.1、定积分的分部积分法&mdash;&mdash;基本计算</p>\n<p>3.2、定积分的分部积分法&mdash;&mdash;华莱士公式</p>\n<p>第二十二讲 积分计算综合</p>\n<p>1、问题引入</p>\n<p>2、几类积分计算总结</p>\n<p>3、奇偶函数的定积分</p>\n<p>4、周期函数的定积分</p>\n<p>第二十三讲 定积分的几何应用</p>\n<p>1、问题引入</p>\n<p>2.1、平面图形的面积&mdash;&mdash;面积的积分表示</p>\n<p>2.2、平面图形的面积&mdash;&mdash;面积的计算</p>\n<p>3.1、体积&mdash;&mdash;已知截面面积立体的体积</p>\n<p>3.2、体积&mdash;&mdash;已知截面面积立体的体积</p>\n<p>第二十四讲 定积分的物理应用</p>\n<p>1、、问题引入</p>\n<p>2、功</p>\n<p>3、静压力</p>\n<p>4、引力</p>\n<p>第二十五讲 反常积分讲</p>\n<p>1、问题引入</p>\n<p>2、无穷区间的反常积分</p>\n<p>3、无界函数的反常积分</p>\n<p>4、反常积分的敛散性</p>\n<p>第二十六讲 定积分的数值计算</p>\n<p>1、问题引入</p>\n<p>2、数值积分的基本思想</p>\n<p>3、矩形公式</p>\n<p>4、梯形公式</p>\n<p>5、辛普森公式</p>\n</div>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">预备知识</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>高等数学（一）</p>\n</div>\n</div>\n<div id=\"requirements\" class=\"category-title f-f0 certRope\">&nbsp;<span class=\"f-ib f-vam\">证书要求</span></div>\n<div class=\"category-content j-cover-overflow certRope\">\n<div class=\"f-richEditorText\">\n<p>本课程采用百分制计分，包括单元测验、讨论和期末考试。其中：</p>\n<p>单元测验为客观题，占总成绩的30%；</p>\n<p>讨论部分占总成绩的10%，在&ldquo;课堂讨论区&rdquo;回复10个及以上的帖子，可以得到讨论部分的满分；</p>\n<p>期末考试为客观题，占总成绩的60%。</p>\n<p>总成绩：&ldquo;60&le;得分&lt;85&rdquo;的学员可申请合格证书，&ldquo;85&le;得分&rdquo;的学员可申请优秀学员证书。</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">参考资料</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>【1】朱健民，李建平. 高等数学（第二版）（上、下）.北京：高等教育出版社，2015年.</p>\n<p>&nbsp;</p>\n<p><img id=\"1\" src=\"https://nos.netease.com/edu-image/54918672036603F02F2DDE9C288500E3.jpg?imageView&amp;thumbnail=520x520&amp;quality=100\" /></p>\n<p>上册购买链接：<a href=\"https://www.hepmall.com/index.php/product-16589.html\" target=\"_blank\" rel=\"noopener\">https://www.hepmall.com/index.php/product-16589.html</a></p>\n<p>&nbsp;</p>\n<p>下册购买链接：<a href=\"https://www.hepmall.com/index.php/product-16799.html\" target=\"_blank\" rel=\"noopener\">https://www.hepmall.com/index.php/product-16799.html</a></p>\n<p>&nbsp;</p>\n<p>【2】李建平，朱健民. 高等数学的典型例题与解法（上、下）.长沙：国防科技大学出版社，2003年.</p>\n<p>【3】李建平，朱健民等. 高等数学课程实验.北京：科学出版社，2011年.</p>\n<p>【4】李建平，朱健民等. 高等数学试题汇编.长沙：国防科技大学出版社，2013年.</p>\n</div>\n</div>', '2020-11-29 23:28:22', '2020-11-29 23:28:22');
INSERT INTO `edu_course_description` VALUES ('1333071294190755841', '<p>基因是遗传物质的携带者。没有基因的代代相传，就没有物种的稳定；没有基因的表达，就没有个体的生长；没有基因的表达调控，就没有个体的正常发育。你想知道基因是如何保持、表达和调控的吗？你想训练你的科研思维吗？请进入来自武汉大学生命科学学院刘青珍老师的分子生物学课堂&hellip;&hellip;</p>\n<p>&mdash;&mdash; 课程团队</p>\n<p>&nbsp;课程概述</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>本课程在生物化学、遗传学和细胞生物学基本知识的基础上，从生物大分子水平阐述基因组的保持、基因组的表达和基因表达调控的机制。</p>\n<p>本课程授课内容是学生将来从事生物学研究所需掌握的基础理论知识，除了分子生物学基础理论知识，课程还注重培养学生的科研思维能力和训练学生的学术表达能力，因为这两方面对学生的将来非常重要。</p>\n<p>本课程2003年成为&ldquo;国家理科基地名牌课程建设&rdquo;项目，2005年获批&ldquo;武汉大学精品课程建设&rdquo;项目，2006年获批&ldquo;湖北省精品课程&rdquo;和&ldquo;国家精品课程&rdquo;项目，2007年获批&ldquo;教育部分子生物学双语示范课程&rdquo;项目，2011年获批&ldquo;武汉大学分子生物学全英文教学课程&rdquo;项目，2013年获批&ldquo;国家精品资源共享课&rdquo;项目，2015年开始在爱课程网站以MOOC形式开课，并于2017年获批国家精品在线开放课程。</p>\n<p>相信课程一定有许多可以改进的地方。如果你对本课程有任何想法和建议，请你告诉我，我愿意与你一起，探索好的教学方法，不断提高该课程的教学效果，争取能够让你在我的分子生物学课程中有收获、有提高。</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;授课目标</p>\n<ol>\n<li>帮助学生掌握分子生物学基础理论知识，从生物大分子水平理解基因组的保持、基因组</li>\n</ol>\n<p>的表达以及基因表达调控的机制。</p>\n<ol start=\"2\">\n<li>启发和培养学生的科研思维能力。</li>\n<li>加强和提高学生的学术表达能力。</li>\n</ol>\n<p>&nbsp;课程大纲</p>\n<p>第一周 绪论</p>\n<p>第一周-1：几句与基础理论无关的话&mdash;&mdash;旨在使学生明白走好人生的每一步很重要</p>\n<p>第一周-2：传统课堂教学中本课程的做法&mdash;&mdash;使学生直观体会本课程教学方法和目的</p>\n<p>第一周-3：希望和鼓励</p>\n<p>第一周-4：课程介绍（1）&mdash;&mdash; 本课程的建设历史、教材、辅助教材和参考资源</p>\n<p>第一周-5：课程介绍（2）&mdash;&mdash; 本课程的教学内容、日历、方法、目的和考核方式</p>\n<p>第一周-6：第1章、第3-5章内容，使学生体会什么是科学研究和良好的学术表达</p>\n<p>第一周-7：第2章中的经典实验，使学生体会什么是科学研究和良好的学术表达</p>\n<p>第一周-8：本周总结、布置作业</p>\n<p>第一周作业 &nbsp;查阅文献资料，完成小论文</p>\n<p>第二周 基因组结构、染色质和核小体（一）</p>\n<p>第二周-1： 本章前言&mdash;核小体的结构及调控对调控真核细胞中涉及DNA 的多数过程至关重要</p>\n<p>第二周-2：基因组序列和染色体多样性（1）&mdash;环状和线性染色体面临的问题、每个细胞都维持特定的染色体数目</p>\n<p>第二周-3：基因组序列和染色体多样性（2）&mdash;基因组大小、基因数目、基因密度及其与生物复杂度的关系</p>\n<p>第二周-4：基因组序列和染色体多样性（3）&mdash;真核基因密度降低、假基因</p>\n<p>第二周-5：染色体的复制和分离（1）&mdash;细胞分裂中真核染色体正确复制和分离至关重要的几个DNA 元件</p>\n<p>第二周-6：染色体的复制和分离（2）&mdash;真核染色体的复制和分离</p>\n<p>第二周作业</p>\n<p>第三周 基因组结构、染色质和核小体（二）</p>\n<p>第三周-1：核小体&mdash;核小体的组成和结构</p>\n<p>第三周-2：染色质的高级结构&mdash;组蛋白H1和组蛋白N端尾巴与30 nm 纤丝结构</p>\n<p>第三周-3：染色质结构的调控&mdash;核小体修饰、核小体重塑、DNA 的可接近性</p>\n<p>第三周-4：核小体的组装&mdash;核小体的组装、组蛋白在子代染色体中的分布</p>\n<p>第三周作业</p>\n<p>第四周 讨论</p>\n<p>第四周-1：介绍本周讨论1&mdash;老师介绍本周讨论1的背景</p>\n<p>第四周-2：本周讨论1&mdash;学生讨论：DNA复制中两条链分开</p>\n<p>第四周-3：介绍本周讨论2&mdash;老师介绍本周讨论2的背景</p>\n<p>第四周-4：本周讨论2&mdash;学生讨论：多肽合成方向是从氨基端到羧基端</p>\n<p>第四周-5：本周总结&mdash;老师总结本周讨论</p>\n<p>第五周 DNA与RNA的结构</p>\n<p>第五周-1：DNA的结构（1）&mdash;DNA的基本组成和结构</p>\n<p>第五周-2：DNA的结构（2）&mdash;碱基翻出、DNA双螺旋</p>\n<p>第五周-3：DNA的结构（3）&mdash;DNA变性和复性</p>\n<p>第五周-4：DNA 拓扑学（1）&mdash;DNA 拓扑学的基本知识</p>\n<p>第五周-5：DNA 拓扑学（2）&mdash;拓扑异构酶</p>\n<p>第五周-6：RNA 的结构&mdash;RNA 的组成和结构特点</p>\n<p>第五周作业</p>\n<p>第六周 DNA的复制（一）</p>\n<p>第六周-1：DNA 合成的化学基础&mdash;DNA复制的底物、产物以及反应中的能量变化</p>\n<p>第六周-2：DNA 聚合酶的作用机制 （1）&mdash;DNA聚合酶识别4种dNTP及dNTP和rNTP的机制</p>\n<p>第六周-3：DNA 聚合酶的作用机制 （2）&mdash;DNA聚合酶的结构及其在催化DNA合成中的作用机制</p>\n<p>第六周-4：DNA 聚合酶的作用机制 （3）&mdash;DNA聚合酶的延伸性和校正功能</p>\n<p>第六周-5：复制叉（1）&mdash;复制叉上两条链同时合成；RNA引物的产生和去除</p>\n<p>第六周-6：复制叉（2）&mdash;在复制叉处多种蛋白协同工作</p>\n<p>第六周作业</p>\n<p>第七周 DNA的复制（二）</p>\n<p>第七周-1：DNA聚合酶的特化&mdash;不同DNA聚合酶、滑动夹和滑动夹装载器</p>\n<p>第七周-2：DNA 在复制叉上的合成&mdash;DNA是如何在复制叉处被合成的</p>\n<p>第七周-3：DNA 复制的起始&mdash;DNA复制起始相关元件</p>\n<p>第七周-4：复制起始位点的选择和激活（1）&mdash;大肠杆菌DNA 复制起始的模型</p>\n<p>第七周-5：复制起始位点的选择和激活（2）&mdash;真核染色体每个细胞周期复制一次的机制</p>\n<p>第七周-6：结束复制&mdash;DNA 末端复制问题及解决方法</p>\n<p>第七周作业</p>\n<p>第八周 讨论课</p>\n<p>第八周-1：课程讨论&mdash;课程教学方法、教学目的和学习方法的讨论</p>\n<p>第八周-2：小结&mdash;第六、七章小结</p>\n<p>第八周-3：讨论介绍&mdash;老师引出讨论内容</p>\n<p>第八周-4：讨论&mdash;Mixing or Not Mixing</p>\n<p>第八周-5：讨论小结&mdash;老师对本周讨论的小结</p>\n<p>第九周</p>\n<p>第九周-1：RNA 聚合酶和转录周期&mdash;RNA 聚合酶的结构特征、转录周期的大致过程</p>\n<p>第九周-2：细菌的转录周期（1）&mdash;&sigma;70启动子及其与启动子的关系</p>\n<p>第九周-3：细菌的转录周期（2）&mdash;细菌转录的起始、延伸、终止；终止子</p>\n<p>第九周-4：真核细胞转录（1）&mdash;RNAPⅡ核心启动子、RNAPⅡ转录的起始过程</p>\n<p>第九周-5：真核细胞转录（2）&mdash;RNAPⅡ的延伸、终止</p>\n<p>第九周-6：RNA 聚合酶Ⅰ和Ⅲ催化的转录</p>\n<p>第九周作业</p>\n<p>第十周 RNA剪接</p>\n<p>第十周-1：RNA 剪接的化学基础</p>\n<p>第十周-2：剪接体</p>\n<p>第十周-3：剪接过程（1）&mdash;剪接过程</p>\n<p>第十周-4：剪接过程（2）&mdash;剪接的准确性</p>\n<p>第十周-5：选择性（可变）剪接</p>\n<p>第十周-6：RNA 编辑</p>\n<p>第十周-7：mRNA 转运</p>\n<p>第十周-8：遗传密码&mdash;遗传密码的特点、抑制突变</p>\n<p>第十周作业</p>\n<p>第十一周 基因组的表达中的重要一步&mdash;&mdash;翻译</p>\n<p>第十一周-1：本章概要</p>\n<p>第十一周-2：信使RNA</p>\n<p>第十一周-3：转移RNA</p>\n<p>第十一周-4：氨基酸在tRNA上的加载</p>\n<p>第十一周-5：核糖体</p>\n<p>第十一周-6：翻译起始</p>\n<p>第十一周-7：翻译延伸</p>\n<p>第十一周-8：翻译终止</p>\n<p>第十一周-9：依赖翻译的mRNA和蛋白质稳定性调控</p>\n<p>第十一周作业</p>\n<p>第十二周 基因组的调控</p>\n<p>第十二周-1：本章概要</p>\n<p>第十二周-2：转录调控的原理</p>\n<p>第十二周-3：转录起始的调控：细菌中的实例（1）&mdash;乳糖操纵子</p>\n<p>第十二周-4：转录起始的调控：细菌中的实例（2）&mdash;通过使用不同的s 因子进行的调控</p>\n<p>第十二周-5：转录起始的调控：细菌中的实例（3）&mdash;NtrC和MerR</p>\n<p>第十二周-6：转录起始的调控：细菌中的实例（4）&mdash;araBAD 操纵子</p>\n<p>第十二周-7：&lambda; 噬菌体的调控</p>\n<p>十二周作业</p>\n<p>第十三周 真核生物的基因表达调控</p>\n<p>第十三周-1：本章概要</p>\n<p>第十三周-2. 从酵母到哺乳动物转录调控的保守机制</p>\n<p>第十三周-3. 真核生物活化子募集蛋白复合物到基因上</p>\n<p>第十三周-4. 信号整合与组合调控</p>\n<p>第十三周-5. 转录抑制子</p>\n<p>第十三周-6. 信号转导与转录调控蛋白的控制</p>\n<p>第十三周-7. 组蛋白与DNA 修饰导致的基因&ldquo;沉默&rdquo;</p>\n<p>第十三周-8. 基因的表观遗传调控</p>\n<p>十三周作业</p>\n<p>第十四周 &nbsp;特殊的RNA - 调控RNA</p>\n<p>第十四周-1：本周内容</p>\n<p>第十四周-2： 细菌中RNA 介导的调控</p>\n<p>第十四周-3：干扰是真核生物的主要调控机制</p>\n<p>第十四周-4：miRNA 的合成与功能</p>\n<p>第十四周-5：介绍&ldquo;DNA 末端复制问题&rdquo;背景</p>\n<p>第十四周-6 ：讨论：DNA 末端复制问题</p>\n<p>第十四周-7： &ldquo;DNA 末端复制问题&rdquo;小结</p>\n<p>第十四周-8：课程总结</p>\n<p>十四周作业</p>\n<p>&nbsp;预备知识</p>\n<p>生物化学、遗传学和细胞生物学基本知识</p>\n<p>&nbsp;证书要求</p>\n<p>作&nbsp; 业：40％</p>\n<p>期末考试：55％</p>\n<p>讨&nbsp; 论：5％</p>\n<p>加&nbsp; 分：1-5%：奖励对同学们的学习或本课程的教学有突出贡献者</p>\n<p>&nbsp;</p>\n<p>60分为合格，80分及以上为优秀</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;参考资料</p>\n<p><strong>教</strong><strong>&nbsp; </strong><strong>材：</strong></p>\n<p>Molecular Biology of the Gene （第六版）</p>\n<p>Cold Spring Harbor Press出版， 2007年出版</p>\n<p>James Watson, Tania A. Baker, Stephen P. Bell, Alexander Gann, Michael Levine, Richard Losick</p>\n<p>0-321-50781-9、 978-0-321-50781-5&nbsp;&nbsp;</p>\n<p>&nbsp;</p>\n<p><strong>辅助教材：</strong></p>\n<p>基因的分子生物学（第6版）</p>\n<p>科学出版社</p>\n<p>杨焕明 (译者), 等 (译者)</p>\n<p>ISBN：978-7-030-24756-8&nbsp;</p>\n<p>&nbsp;</p>\n<p><strong>参考资料</strong></p>\n<ol>\n<li>Instant Notes in Molecular Biology（第二版），英国Bios科学出版社出版，Turner等编著，2000年版。</li>\n<li>Molecular Biology（第三版），McGraw-Hill出版社，Robert Weaver著，2005年版。</li>\n</ol>\n<p>3.分子生物学 (原书第四版)，科学出版社，郑用链主译，2010.1.</p>\n<p>4.现代分子生物学（第三版），高等教育出版社，朱玉贤、李毅、郑晓峰，2007.11.</p>\n<p>5.分子生物学，高等教育出版社，陈启民、耿运琪，2010.3.</p>\n<p>6.分子生物学教程(第三版)，科学出版社，赵亚华，2011.</p>\n<p>7.分子生物学（第3版中译版），科学出版社，译校：刘进元、刘文颖，2010.8.</p>\n<p>8.分子生物学，化学工业出版社，杨建雄，2009.4.</p>\n<p>9.基础分子生物学，科学出版社，叶林柏、郜金荣，2004.</p>', '2020-11-29 23:32:19', '2020-11-29 23:32:19');
INSERT INTO `edu_course_description` VALUES ('1333076173844029442', '<div id=\"j-course-heading-intro\" class=\"course-heading-intro\">\n<div id=\"j-rectxt2\" class=\"course-heading-intro_intro\">《西方音乐欣赏》成立伊始就成为北京大学最受欢迎的课程之一，而这门课程的成功之处在于：原本学生认为&ldquo;做音乐&rdquo;这件事是遥不可及的，在听完课后，觉得可以试一试。</div>\n<div class=\"course-heading-intro_team\">&mdash;&mdash; 课程团队</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">课程概述</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>1.着眼中国大学生普遍存在的前大学阶段音乐教育整体薄弱问题，强调聆听方法，培养欣赏能力，正本清源，查漏补缺；</p>\n<p>2.着眼西方学界已达成共识之一级知识，去除以往过多个人化色彩，令中国学生在有限时间内，与西方学生在此课程基础知识方面同步，为其艺术通识水平的提升，夯实基础，避免弯路；</p>\n<p>3.着眼对中国同类课程历史与现状的经验与教训之总结，承继朱光潜先生&ldquo;中西互证&rdquo;的北大学术传统，凸显多学科渗透与交叉，令学生在获得相关领域研究前沿信息的同时，发展其美感体验、美学思维与美育人生之融会贯通；</p>\n<p>4.着眼核心作品的精听精解，寓教于乐，体系清晰，重点突出，取精用弘，深入浅出，以&ldquo;如何听歌-如何听曲&rdquo;为两大技术支点，以&ldquo;人声音色-器乐音色&rdquo;为两大知识支柱，根据时代风格变迁，引导学生掌握聆听与欣赏口诀，根据知识分类分层，培养学生对相关术语与文献的使用能力；</p>\n<p>5.着眼现代，背靠传统，面向西方，立足中国，以慕课、多媒体课堂与讲座音乐会的方式，赋予课程当下意义，令学生在理性与感性的结合、聆听与欣赏的并进、审美与创造一体的前提下，更为全面地认识自己、认识中国、认识西方、认识世界。</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">授课目标</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>1、有效发展学生西方音乐的聆听与欣赏能力&nbsp;</p>\n<p>2、拓展学生跨学科兴趣的广度与深度</p>\n<p>&nbsp;3、培养学生的独立思考与综合分析能力的全面发展&nbsp;</p>\n<p>4、强调通识教育的精读与泛读并进的学习模式&nbsp;</p>\n<p>5、提升学生情感认知能力，增强学生关于欣赏及美感的理解能力</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">课程大纲</span></div>\n<div id=\"j-course-outline\" class=\"category-content j-cover-overflow\">\n<div class=\"outline\">\n<div class=\"f-richEditorText\">\n<p>第一讲：绪论（一）<br />1-如何与音乐交朋友：核心问题<br />2-人类听觉认知的基本顺序<br />3-健脑化的欣赏<br />4-科学化的欣赏<br />5-实践化的欣赏<br />6-精锐化的欣赏<br />7-音乐欣赏的个体化与差异化</p>\n<p>第二讲：绪论（二）<br />1-西方音乐的历史线索<br />2-西方音乐的风格构成<br />3-音乐风格的统一性-复杂性-独特性<br />4-莫扎特《第四十交响曲》精听<br />5-西方音乐欣赏的七点忠告和核心目标<br />6-西方音乐欣赏的三点关怀和第一起点<br />7-贝多芬《第五交响曲&middot;第一乐章》精听<br />作业1<br />&nbsp;&nbsp;<br />第三讲：如何听歌（一）<br />1-人声音乐的概念与定位<br />2-听歌五识法的步骤（1）<br />3-听歌欣赏的过程<br />4-《阿玛丽莉》精听<br />5-《玫瑰三愿》精听<br />6-《爱之路》精听<br />7-《风雨飘摇》精听</p>\n<p>第四讲：如何听歌（二）<br />1-听歌五识法的步骤（2）<br />2-文体-文心-乐体-乐心<br />3-旋律与结构的规则<br />4-女高音-女中音-热点-燃点-沸点<br />5-《赛基洛斯墓志铭》精听<br />6-《我非要唱》精听<br />7-《因斯布鲁克，我必须离开你》精听&nbsp;<br />作业2<br />&nbsp;<br />第五讲：如何听歌（三）<br />1-听歌五识法的步骤（3）<br />2-歌曲的&ldquo;乱&rdquo;与&ldquo;花&rdquo;：高潮与装饰<br />3-歌曲的&ldquo;情&rdquo;与&ldquo;腔&rdquo;：对比与语气<br />4-《你多么美丽》精听<br />5-《热泪奔流》精听<br />6-《月迷彼埃罗&middot;夜》精听<br />7-《和尚与猫》精听</p>\n<p>第六讲：如何听歌（四）<br />1-听歌五识法的步骤（4）<br />2-男高音-男中音-假声男高音<br />3-《我见到我太阳》精听<br />4-《菩提树》精听<br />5-《水边的格丽岑》精听<br />6-《魔王》精听<br />7-《我泪流》精听<br />作业3</p>\n<p>第七讲：如何听歌（五）<br />1-从单声到多声-从俗乐到圣乐<br />2-听歌五识法到听歌七识法（1）<br />3-歌剧-清唱剧-康塔塔<br />4-《海上扁舟》与《银浪漂泊》比较精听<br />5-海顿《创世纪》精听<br />6-巴赫《第四康塔塔》精听<br />7-莫扎特《安魂曲》精听</p>\n<p>第八讲：如何听歌（六）<br />1-器乐伴奏-模仿造型-独奏发展<br />2-听歌五识法到听歌七识法（2）<br />3-歌剧类型化-角色化-风格化-地方化<br />4-亨德尔女高音-女中音<br />5-莫扎特女高音<br />6-罗西尼-威尔第-普契尼女高音-男高音<br />7-瓦格纳女高音-男高音-标签法<br />总结：兼论三大语种歌剧<br />作业4<br />&nbsp;<br />第九讲：如何听曲（一）<br />1-西方音乐音响的总体特征<br />2-西方音乐风格的历史对照<br />3-从伴奏到独奏，为诗歌配伴奏：器乐模仿法<br />4-维瓦尔第《四季》精听<br />5-从单歌到组歌-从单曲到组曲-从有词歌到无词歌<br />6-巴赫《第二管弦乐组曲》与亨德尔《水上组曲》精听<br />7-勃拉姆斯《大学组曲》精听</p>\n<p>第十讲：如何听曲（二）<br />1-器乐音乐的顺序链与聆听点<br />2-器乐音乐的调性-音型-织体-低音<br />3-听曲七定法与乐器分类法（1）<br />4-布里顿《青少年管弦乐指南》精听<br />5-拉威尔《波列罗》精听<br />6-贝多芬《第九交响曲&middot;末乐章》精听<br />7-柏辽兹《幻想交响曲》精听-标签法<br />作业5</p>\n<p>第十一讲：如何听曲（三）<br />1-听曲七定法与乐器分类法（2）<br />2-西方器乐风格发展的四大过程-四大手法<br />3-西方器乐形式的四大种类和四大场所<br />4-巴赫《布兰登堡协奏曲》精听<br />5-比才《卡门》家族综括<br />6-贝多芬钢琴奏鸣曲综括<br />7-贝多芬代表钢琴奏鸣曲之一精听<br />&nbsp;<br />第十二讲：如何听曲（四）<br />1-听曲七定法与乐器分类法（3）<br />2-膜鸣与体鸣器乐音色家族<br />3-穆索尔斯基-拉威尔《图书展览会》精听<br />4-赖克《鼓声》精听<br />5-约翰&middot;凯奇《起居室音乐》精听<br />6-威廉&middot;卡拉夫特《相遇：小号与打击乐》精听<br />7-巴托克《为弦乐、打击乐、钢片琴而作的音乐》精听<br />作业6</p>\n<p>&nbsp;<br />第十三讲：如何听曲（五）<br />1-听曲七定法与乐器分类法（4）<br />2-气鸣器乐音色家族<br />3-普罗科菲耶夫《D大调长笛协奏曲》精听<br />4-莫扎特《A大调单簧管协奏曲》精听<br />5-亨德尔《快乐铁匠-双簧管-大管-羽管键琴变奏曲》精听<br />6-维瓦尔第《双小号协奏曲》精听<br />7-普朗克《圆号-小号-长号协奏曲》精听</p>\n<p>第十四讲：如何听曲（六）<br />1-听曲七定法与乐器分类法（5）<br />2-弦鸣器乐音色家族（1）<br />3-巴赫《哥德堡变奏曲》（吉他版-羽管键琴版-钢琴版-乐队版）精听<br />4-罗多里格Rodrigo《阿兰胡埃斯吉他协奏曲》精听<br />5-贝多芬《D大调小提琴协奏曲》精听<br />6-帕格尼尼《小提琴随想曲》之一精听<br />7-德沃夏克《大提琴协奏曲》精听<br />8-爵士乐三重奏样本精听<br />作业7</p>\n<p><br />第十五讲：如何听曲（七）<br />1-听曲七定法与乐器分类法（6）<br />2-弦鸣器乐音色家族（2）<br />3-德奥钢琴音乐综括<br />4-莫扎特代表钢琴协奏曲之一精听<br />5-贝多芬代表钢琴协奏曲之一精听<br />6-俄罗斯钢琴协奏曲综括<br />7-柴可夫斯基代表钢琴协奏曲之一精听<br />&nbsp;</p>\n<p>第十六讲：如何听曲（八）<br />1-听曲七定法与乐器分类法（7）&nbsp;<br />2-交响-管弦乐团的现当代发展<br />3-巴托克《神奇的满大人》精听<br />4-斯特拉文斯基《春之祭》精听<br />5-梅西昂《图朗加里拉》精听<br />6-电子器乐音色家族<br />7-科技影响下的西方音乐<br />总结：中西器乐思维的异同与互证<br />作业8及考试</p>\n<p>&nbsp;</p>\n</div>\n</div>\n</div>\n<div id=\"requirements\" class=\"category-title f-f0 certRope\">&nbsp;<span class=\"f-ib f-vam\">证书要求</span></div>\n<div class=\"category-content j-cover-overflow certRope\">\n<div class=\"f-richEditorText\">\n<p>本学期的成绩评定方式：</p>\n<p>&nbsp;</p>\n<p>单元测验：每节慕课课程之后包括10~30个单选题，只允许尝试1 次，按照题目比例得分。</p>\n<p>&nbsp;</p>\n<p>学生总成绩为：各次单元测试的平均值，60 分及以上合格，85 分及以上优秀。</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">参考资料</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>1.The Enjoyment of Music: : An Introduction of Perceptive Listening Joseph Machlis &amp; Kristine Forney Standard, W.W. Norton &amp; Company, 1955-2014&nbsp;&nbsp;&nbsp;&nbsp; <br />2.音乐鉴赏 毕明辉 高等教育出版社</p>\n<p>3.An Outline History of Western Music Milo Wold, Gary Martin, James Miller, Edmund Cykler WCB McGraw-Hill Press,1976-1998&nbsp;&nbsp;&nbsp;&nbsp; <br />4.The New Grove Dictionary of Music and Musicians Stanley Sadie &amp; John Tyrrell MCM2000&nbsp;&nbsp;&nbsp;&nbsp; <br />5.Norton Anthology of Western Music Peter Burkholder &amp; Claude Palisca W.W. Norton &amp; Company, 1980-2006&nbsp;&nbsp;&nbsp;&nbsp; <br />6.Study and Listening Guide for Concise History of Western Music by B. R. Hanning &amp; Norton Anthology of Western Music by C. Palisca Peter Burkholder W.W. Norton &amp; Company, 1998-2006&nbsp;&nbsp;&nbsp;&nbsp; <br />7.Study and Listening Guide: for A History of Western Music, Eighth Edition and Norton Anthology of Western Music Peter Burkholder W.W. Norton &amp; Company, 1998-2009&nbsp;&nbsp;&nbsp;&nbsp; <br />8.The History of Music Instruments Kurt Sachs Dover Co. 2006</p>\n</div>\n</div>', '2020-11-29 23:51:42', '2020-11-29 23:51:42');
INSERT INTO `edu_course_description` VALUES ('1333080194101190658', '<div id=\"j-course-heading-intro\" class=\"course-heading-intro\">\n<div id=\"j-rectxt2\" class=\"course-heading-intro_intro\">《C语言程序设计》课程以程序设计为主线，以培养初学者的分析问题能力、解决问题能力及程序设计能力为目标。本课程强调实用性，注重理论与实践相结合，通过大量的例题，深入浅出，循序渐进地讲解C语言的语法规则与实际应用，并注重培养初学者良好的程序设计风格。</div>\n<div class=\"course-heading-intro_team\">&mdash;&mdash; 课程团队</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">课程概述</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>&nbsp;</p>\n<p>C语言是一门结构化程序设计语言，是计算机程序语言的基础，兼有高级语言和低级语言的功能，不仅可用于编写应用软件，还可以编写系统软件以及嵌入式系统。目前流行的面向对象程序设计语言，如C++、Java、C#等都是在C语言的基础上发展派生而来的。通过学习C语言不仅能够掌握程序设计的基本思想，也可为今后学习Java、C++、VB等语言打下良好的基础。</p>\n<p>C语言可以用于开发比较底层的东西，比如驱动、通信协议之类，在Unix和Linux环境中也是不可或缺的，另外在嵌入式领域也大有作为。</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp; 本课程讲解C语言的基本数据类型及其运算、分支结构、循环结构、数组、函数、结构体、指针和文件等内容。</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp; 如果懂得C，就能很好地理解程序设计的思想，理解如何构造数组、链表、树、图等结构，才能体验程序设计的思想和算法的精髓。</p>\n<p><br /><br /></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">授课目标</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>通过本课程的学习，使学生能够熟练地运用结构化程序设计方法设计编写、调试和运行C语言程序。 &nbsp; &nbsp; &nbsp; 本课程从理论和实践两个方面培养和提高学生程序设计能力，以及应用计算思维方法去分析和解决问题的能力，为学生在今后的学习和工作中能够很好地使用计算机来解决实际问题打下良好的基础。</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">课程大纲</span></div>\n<div id=\"j-course-outline\" class=\"category-content j-cover-overflow\">\n<div class=\"outline\">\n<div class=\"f-richEditorText\">\n<p>第1周 程序设计与C语言</p>\n<p>1.1 程序设计语言及其发展</p>\n<p>1.2 C语言的发展及其特点</p>\n<p>1.3 简单的C程序介绍</p>\n<p>1.4 C程序的上机步骤</p>\n<p>第1周编程练习</p>\n<p>第2周 程序的数据描述&mdash;&mdash;基本数据类型</p>\n<p>2.1 C语言基本元素</p>\n<p>2.2 C语言数据类型</p>\n<p>2.3 常量和变量</p>\n<p>2.4 整型数据</p>\n<p>2.5 实型数据</p>\n<p>2.6 字符型数据</p>\n<p>第3周 程序的数据运算</p>\n<p>3.1 运算符与表达式</p>\n<p>3.2 算术运算</p>\n<p>3.3 赋值运算</p>\n<p>3.4 自增和自减运算及强制类型转换</p>\n<p>第3周编程练习</p>\n<p>第4周 顺序结构程序设计</p>\n<p>4.1 程序的基本结构</p>\n<p>4.2 C语句概述</p>\n<p>4.3 字符数据输入输出</p>\n<p>4.4 数据格式化输入输出</p>\n<p>第1次单元测验</p>\n<p>第5周 选择结构程序设计</p>\n<p>5.1 关系运算符和关系表达式</p>\n<p>5.2 逻辑运算符和逻辑表达式</p>\n<p>5.3 if 语句</p>\n<p>5.4 switch语句</p>\n<p>5.5 条件运算符和条件表达式</p>\n<p>5.6 选择结构程序举例</p>\n<p>第6周 循环结构程序设计</p>\n<p>6.1 while语句</p>\n<p>6.2 do&hellip;while语句</p>\n<p>6.3 for语句</p>\n<p>6.4 循环嵌套</p>\n<p>6.5 辅助控制语句</p>\n<p>6.6 循环结构实例</p>\n<p>第2次单元测验</p>\n<p>第7周 同类型数据的处理&mdash;&mdash;数组（一）</p>\n<p>7.1 同类数据处理问题</p>\n<p>7.2 一维数组的定义和引用</p>\n<p>7.3 一维数组的应用</p>\n<p>7.4 二维数组定义和引用</p>\n<p>7.5 二维数组的应用</p>\n<p>7.6 数组下标越界访问问题</p>\n<p>第8周 同类型数据的处理&mdash;&mdash;数组（二）</p>\n<p>7.7 字符数组定义及输入输出</p>\n<p>7.8 字符串处理函数</p>\n<p>7.9 字符数组应用举例</p>\n<p>第3次单元测验</p>\n<p>第9周 模块化程序设计&mdash;&mdash;函数（一）</p>\n<p>8.1 概述</p>\n<p>8.2 函数定义与调用</p>\n<p>8.3 函数的参数传递</p>\n<p>8.4 数组作为函数参数</p>\n<p>第10周 模块化程序设计&mdash;&mdash;函数（二）</p>\n<p>8.5 函数的嵌套调用与递归调用</p>\n<p>8.6 变量的作用域</p>\n<p>8.7变量的存储类别</p>\n<p>第4次单元测验</p>\n<p>第11周 C语言灵魂&mdash;&mdash;指针（一）</p>\n<p>9.1 地址和指针的基本概念</p>\n<p>9.2 指针变量的定义与引用</p>\n<p>9.3 指针和数组</p>\n<p>第12周 C语言的灵魂&mdash;&mdash;指针（二）</p>\n<p>9.4 指针和字符串</p>\n<p>9.5 指针与函数</p>\n<p>9.6 指针数组和指向指针的指针变量</p>\n<p>第13周 不同类型数据的处理&mdash;&mdash;结构体和共用体</p>\n<p>10.1 结构体类型的定义</p>\n<p>10.2 结构体变量</p>\n<p>10.3 结构体指针</p>\n<p>10.4 向函数传递结构体</p>\n<p>10.5 共用体</p>\n<p>11.1 *链表概述</p>\n<p>11.2 *单链表建立</p>\n<p>11.3 *单链表输出</p>\n<p>11.4 *单链表查找</p>\n<p>11.5 *单链表删除</p>\n<p>11.6 *单链表插入</p>\n<p>第5次单元测验</p>\n<p>第14周 文件</p>\n<p>12.1 文件概述</p>\n<p>12.2 文件的打开和关闭</p>\n<p>12.3 文件的顺序读写</p>\n<p>12.4 文件的随机读写</p>\n</div>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">预备知识</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 本课程可以零基础学习。大学计算机基础、计算思维导论等课程对于理解本课程的部分内容有帮助，但这些课程的学习不是必须的。</p>\n</div>\n</div>\n<div id=\"requirements\" class=\"category-title f-f0 certRope\">&nbsp;<span class=\"f-ib f-vam\">证书要求</span></div>\n<div class=\"category-content j-cover-overflow certRope\">\n<div class=\"f-richEditorText\">\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp; 本门课程总成绩100分。</p>\n<p>&nbsp;&nbsp; </p>\n<p>&nbsp;&nbsp; 课程的总分达到60分及以上，可以申请本课程的合格证书。</p>\n<p>&nbsp;&nbsp; 课程的总分达到80分及以上，可以申请本课程的优秀证书。</p>\n<p>&nbsp; <strong>&nbsp;为了保障证书权威性，平台不再支持免费电子证书，只提供认证证书。</strong></p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">参考资料</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>&nbsp;</p>\n<p>《C语言程序设计教程》，张玉春等编著，清华大学出版社</p>\n<p>《C语言程序设计实验指导与习题解析》，张玉春等编著，清华大学出版社</p>\n<p>《C程序设计》（第四版），谭浩强著，清华大学出版社</p>\n<p>《C程序设计题解与上机指导》（第四版），谭浩强著，清华大学出版社</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">常见问题</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>1. <strong>Q</strong><strong>：C语言程序上机调式使用什么软件？</strong></p>\n<p>&nbsp;&nbsp;&nbsp; A：使用任意C语言开发工具都可以，在windows上常用的有：VC/VS &nbsp; Dev-C &nbsp;Code::blocks &nbsp;C-Free &nbsp;TC，课程演示会使用DEV-C++和Visual C++6.0集成开发环境。</p>\n<p>&nbsp;</p>\n<p>2.&nbsp;<strong>Q</strong><strong>：如何学好C语言？</strong></p>\n<p>&nbsp;&nbsp; A：C语言是门程序设计语言，可以用它来编写计算机程序。所以要想学好C语言，就必须多动手，多编写程序并在计算机上调式运行。</p>\n</div>\n</div>', '2020-11-30 00:07:40', '2020-11-30 00:07:40');
INSERT INTO `edu_course_description` VALUES ('1333083069682458626', '<div id=\"j-course-heading-intro\" class=\"course-heading-intro\">\n<div id=\"j-rectxt2\" class=\"course-heading-intro_intro\">身处数据时代，如何在&ldquo;数据爆炸但知识缺乏&rdquo;的社会中沙里淘金。数据挖掘作为一门交叉学科，融合了算法、数据库、统计学、信息技术等诸多知识，不仅提供了很好的解决思路，也是有力的科研工具和人工智能发展的重要基础。思维上对数据敏感、实践中熟练掌握工具已经成为了当代数据分析人才的必备素质。</div>\n<div class=\"course-heading-intro_team\">&mdash;&mdash; 课程团队</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">课程概述</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>&nbsp;</p>\n<p>《数据挖掘与Python实践》课程主要讲授数据挖掘的基本理论、各个分支及应用。课程涵盖DM的定义、流程、数据预处理、数据可视化、关联分析、聚类、分类、数值预测等内容。通过课程学习，你将会掌握数据挖掘的基本理论、思想和方法，并通过简单易用的软件来解决相关的问题。</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">授课目标</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>1.&nbsp; 了解和掌握数据挖掘的基本理论、流程和方法。</p>\n<p>2.&nbsp; 了解和掌握数据挖掘过程过中的技术，如数据预处理、数据可视化、关联分析、聚类、分类、数值预测等内容。</p>\n<p>3.&nbsp; 通过课程学习，能通过简单易用的软件来解决数据挖掘的相关问题。</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">课程大纲</span></div>\n<div id=\"j-course-outline\" class=\"category-content j-cover-overflow\">\n<div class=\"outline\">\n<div class=\"outline__new-outline\">\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">01</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">导言</span></div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">1.1数据挖据的产生、定义和流程</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">1.2数据挖掘的方法</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">1.3数据挖掘的应用</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">1.4python介绍</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第一单元测验</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">02</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">认识数据</span></div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">2.1数据对象与属性类型</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">2.2数据的基本统计描述</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">2.3数据可视化</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">2.4数据的相似性与相异性度量</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">2.5python操作：认识数据（一）</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">2.6python操作：认识数据（二）</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第二章单元测验</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">03</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">数据预处理</span></div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">3.1数据预处理的基本思想和数据规范化</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">3.2数据离散化</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">3.3数据清洗</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">3.4特征提取与特征选择</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第三单元测验</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">04</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">关联规则挖掘</span></div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">4.1引言</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">4.2频繁模式与关联规则</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">4.3频繁项集的典型挖掘方法</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">4.4关联规则的生成方法</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">4.5关联规则的其他类型</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">4.6关联规则的兴趣度的其他度量</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">4.7python操作：关联规则挖掘</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第四单元测验</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">05</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">分类</span></div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">5.1分类的概念</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">5.2决策树分类</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">5.3朴素贝叶斯分类</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">5.4k近邻分类</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">5.5分类性能的度量方法</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">5.6python操作：分类（一）</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">5.7python操作：分类（二）</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第五单元测验</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">06</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">数值预测</span></div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">6.1数值预测概念</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">6.2回归方法</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">6.3 回归树与模型树</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">6.4K近邻数值预测和预测误差的度量</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">6.5python操作：数值预测</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第六单元测验</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">07</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">聚类分析</span></div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">7.1概述</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">7.2相似度度量方法</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">7.3 k均值聚类法</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">7.4 层次聚类方法</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">7.5 DBSCAN方法</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">7.6 聚类效果衡量方法</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">7.7python操作：聚类分析（一）</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">7.8python操作：聚类分析（二）</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第七单元测验</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">08</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">复杂数据的挖掘</span></div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">8.1挖掘复杂的数据类型-序列与网络</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">8.2挖掘复杂的数据类型-其他类型</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第八单元测验</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">预备知识</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<ol class=\" list-paddingleft-2\">\n<li>\n<p>数理统计</p>\n</li>\n<li>\n<p>高等代数</p>\n</li>\n</ol>\n</div>\n</div>\n<div id=\"requirements\" class=\"category-title f-f0 certRope\">&nbsp;<span class=\"f-ib f-vam\">证书要求</span></div>\n<div class=\"category-content j-cover-overflow certRope\">\n<div class=\"f-richEditorText\">\n<p>1.考核成绩的构成比例为：平时成绩占40%，期末考试成绩占60%。按百分制计分，60分至84分为合格，85分至100分为优秀。</p>\n<p>&nbsp;</p>\n<p>2.平时成绩分为单元检测与课后讨论两部分，单元检测占平时成绩的50%，课后讨论占平时成绩的50%。</p>\n<p>&nbsp;</p>\n<p>3.单元检测要求同学们在期末考试前的规定时间内做答完毕，每次检测均有3次做答机会，取最高分计入成绩。</p>\n<p>&nbsp;</p>\n<p>4.课后讨论题发布在课堂交流区。课后讨论针对每期老师留的问题，在课堂交流区进行回答。评分机制侧重于发言内容，同学们在课堂交流区内进行5次有效讨论，课后讨论得50分；10次及以上有效讨论，课后讨论得满分100分；没有进行有效讨论得0分。</p>\n<p>&nbsp;</p>\n<p>5.期末考试占总成绩的60%，主要考察同学们对本课程所学内容的掌握程度与应用能力。</p>\n<p>&nbsp;</p>\n<p>注：为对学生负责、保证平台证书权威性，2019年9月开始，中国大学MOOC平台不再发放免费证书，原有认证证书的申请方式和流程不变。</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">参考资料</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>&nbsp;</p>\n<p>1.&nbsp; 《数据挖掘：概念与技术》.韩家炜.（第三版）. 北京：机械工业出版社，2012.</p>\n<p>2.&nbsp; 《商务智能》，刘红岩．北京：清华大学出版社，2013．</p>\n<p>3.&nbsp; 《机器学习》，周志华. 北京：清华大学出版社，2016.</p>\n<p>4.&nbsp; 《统计学习方法》，李航. 北京：清华大学出版社,2019.</p>\n<p>5.&nbsp; 《Python for Data Analysis》，Wes McKinney. O\'Reilly Media, 2019.</p>\n<p>6.&nbsp; 《利用Python进行数据分析》,徐敬一（译）, 北京：机械工业出版社,2018.</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">常见问题</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<ol class=\" list-paddingleft-2\">\n<li>\n<p>课程各单元的发布时间是什么时候？</p>\n<p>课程教学和发布安排一般是每个单元为一周，个别单元为两周，具体时间如下表所示</p>\n<table border=\"0\" width=\"366\" cellspacing=\"0\" cellpadding=\"0\"><colgroup><col width=\"72\" /><col width=\"121\" /><col width=\"173\" /></colgroup>\n<tbody>\n<tr>\n<td width=\"72\" height=\"18\">单元</td>\n<td width=\"121\">单元内容</td>\n<td width=\"173\">发布时间</td>\n</tr>\n<tr>\n<td height=\"18\">第一单元</td>\n<td>导言</td>\n<td>2020/9/6&nbsp; &nbsp;10:00:00</td>\n</tr>\n<tr>\n<td height=\"18\">第二单元</td>\n<td>认识数据</td>\n<td>2020/9/13&nbsp; 10:00:00</td>\n</tr>\n<tr>\n<td height=\"18\">第三单元</td>\n<td>数据预处理</td>\n<td>2020/9/20&nbsp; 10:00:00</td>\n</tr>\n<tr>\n<td height=\"18\">第四单元</td>\n<td>关联规则挖掘</td>\n<td>2020/9/27&nbsp; 10:00:00</td>\n</tr>\n<tr>\n<td height=\"18\">第五单元</td>\n<td>分类</td>\n<td>2020/10/11&nbsp; 10:00:00</td>\n</tr>\n<tr>\n<td height=\"18\">第六单元</td>\n<td>数值预测</td>\n<td>2020/10/25&nbsp; 10:00:00</td>\n</tr>\n<tr>\n<td height=\"18\">第七单元</td>\n<td>聚类分析</td>\n<td>2020/11/1&nbsp; 10:00:00</td>\n</tr>\n<tr>\n<td height=\"19\">第八单元</td>\n<td>复杂数据的挖掘</td>\n<td>2020/11/15 10:00:00</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n</li>\n</ol>\n<p>2.每章测试和期末测试在什么时间？</p>\n<p>&nbsp; 每章测试在每章课程教学发布之后，期末测试在课程结束后一周，2020年11月24号开始-2020年12月6号结束。</p>\n</div>\n</div>', '2020-11-30 00:19:06', '2020-11-30 00:19:06');
INSERT INTO `edu_course_description` VALUES ('1333084014432657410', '<div id=\"j-course-heading-intro\" class=\"course-heading-intro\">\n<div id=\"j-rectxt2\" class=\"course-heading-intro_intro\">数据结构是计算机科学中一门综合性的专业基础课，主要介绍如何合理地组织数据、有效地存储和处理数据，正确地设计算法以及对算法进行分析和评价。本课程的学习将帮助学生深入地理解数据的逻辑结构和物理结构的基本概念以及有关算法，培养基本的、良好的程序设计技能，编制高效可靠的程序。</div>\n<div class=\"course-heading-intro_team\">&mdash;&mdash; 课程团队</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">课程概述</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>&nbsp; &nbsp; &nbsp; &nbsp;数据结构既是先修课程&ldquo;计算机程序设计&rdquo;知识的巩固和扩展，又是后继&ldquo;编译原理&rdquo;、&ldquo;计算机图形学&rdquo;、 &ldquo;计算机网络&rdquo;、&ldquo;人工智能&rdquo;、&ldquo;数字图像处理&rdquo;等课程内容的基础和引导，在专业教学中承担着承前启后、提高专业素养、加强思维能力和强化开发能力的任务。</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp;本课程是一门专业技术基础课，其目的就是要培养学生的数据抽象能力，学会分析研究计算机加工的数据结构的特性，以便为应用涉及的数据选择适当的逻辑结构、存储结构及实现应用的相应算法，并掌握分析算法的时间和空间复杂度的技术。</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp;除通过视频讲解以外，课程团队将借助讨论区提供给授课教师与学习者一个重要的交流方式与交流空间，为广大社会学习者乃至在校学习者答疑解惑，巩固慕课课堂上所学的知识，共同探讨数据结构知识在解决实际问题中的各种可能和实现方案。</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">授课目标</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>通过对数据结构课程的学习，我们应该能够</p>\n<p>&middot; 根据实际应用中对数据处理的要求，为数据选择和建立<strong>合适的</strong>逻辑和存储结构；</p>\n<p>&middot; 选择和使用<strong>较好的</strong>数据处理方法；</p>\n<p>&middot; 利用一种程序设计语言编写出相应的算法；</p>\n<p>&middot; 在计算机系统上调试、运行和实现算法。</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">课程大纲</span></div>\n<div id=\"j-course-outline\" class=\"category-content j-cover-overflow\">\n<div class=\"outline\">\n<div class=\"f-richEditorText\">\n<p>第一章 绪论</p>\n<p>第1讲 基本概念</p>\n<p>第2讲 算法及其分析</p>\n<p>选修：第一章 绪论（完整课件，无视频）</p>\n<p>第一章 绪论单元测验</p>\n<p>第二章 线性表</p>\n<p>第3讲 线性表的顺序表示</p>\n<p>第4讲 线性表的链式表示</p>\n<p>选修：第二章 线性表（完整课件，无视频）</p>\n<p>第二章 线性表单元测验</p>\n<p>第三章 栈和队列</p>\n<p>第5讲 栈</p>\n<p>第6讲 队列</p>\n<p>第30讲 递归的实现</p>\n<p>选修：第三章 栈和队列（完整课件，无视频）</p>\n<p>第三章 栈和队列单元测试</p>\n<p>第五章 数组</p>\n<p>第7讲 数组与特殊矩阵</p>\n<p>第8讲 稀疏矩阵</p>\n<p>选修：第五章 数组（完整课件，无视频）</p>\n<p>第五章 数组单元测试</p>\n<p>第六章 树和二叉树</p>\n<p>第9讲 树与二叉树</p>\n<p>第10讲 二叉树的遍历</p>\n<p>第11讲 线索二叉树</p>\n<p>第12讲 树和森林</p>\n<p>第13讲 赫夫曼树及其应用</p>\n<p>选修：第六章 树和二叉树（完整课件，无视频）</p>\n<p>第六章 树和二叉树单元测试</p>\n<p>第七章 图</p>\n<p>第14讲 图的基本概念</p>\n<p>第15讲 图的存储结构</p>\n<p>第16讲 图的遍历</p>\n<p>第17讲 最小生成树</p>\n<p>第18讲 拓扑排序和关键路径</p>\n<p>第19讲 最短路径</p>\n<p>选修：第七章 图（完整课件，无视频）</p>\n<p>第七章 图单元测试</p>\n<p>第九章 查找</p>\n<p>第20讲 静态查找表</p>\n<p>第21讲 二叉排序树和平衡二叉树</p>\n<p>第22讲 B-树和B+树</p>\n<p>第23讲 哈希表</p>\n<p>选修：第九章 查找（完整课件，无视频）</p>\n<p>第九章 查找单元测验</p>\n<p>第十章 内部排序</p>\n<p>第24讲 内部排序概述</p>\n<p>第25讲 插入排序</p>\n<p>第26讲 交换排序</p>\n<p>第27讲 选择排序</p>\n<p>第28讲 归并排序</p>\n<p>第29讲 基数排序</p>\n<p>选修：第十章 内部排序（完整课件，无视频）</p>\n<p>第十章 排序单元测验</p>\n</div>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">预备知识</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>计算机程序设计、C语言</p>\n</div>\n</div>\n<div id=\"requirements\" class=\"category-title f-f0 certRope\">&nbsp;<span class=\"f-ib f-vam\">证书要求</span></div>\n<div class=\"category-content j-cover-overflow certRope\">\n<div class=\"f-richEditorText\">\n<p>完成所有章节内容的学习，完成至少5次单元测验题，参加期末考试得到60分及以上的成绩即可获得数据结构课程证书，其中：</p>\n<p>85~100&nbsp; &nbsp;优秀</p>\n<p>60~84&nbsp; &nbsp; &nbsp;合格</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">参考资料</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>【1】严蔚敏，吴伟民编著. 数据结构（C语言版）. 北京：清华大学出版社，2018.</p>\n<p>【2】李春葆. 数据结构教程（第4版）学习指导. 北京：清华大学出版社，2013.</p>\n<p>【3】殷人昆. 数据结构精讲与习题详解─考研辅导与答疑解惑. 北京：清华大学出版社，2012.</p>\n</div>\n</div>', '2020-11-30 00:22:51', '2020-11-30 00:22:51');
INSERT INTO `edu_course_description` VALUES ('1333084785412841474', '<div id=\"j-course-heading-intro\" class=\"course-heading-intro\">\n<div id=\"j-rectxt2\" class=\"course-heading-intro_intro\">本课基于主讲教师在北京大学讲授数据结构与算法课（Python版）的多年教学实践经验，面向具有Python语言程序设计基础的大学生和社会公众，介绍常见的基本数据结构以及相关经典算法，强调问题-数据-算法的抽象过程，关注数据结构与算法的时间空间效率，培养学生编写出高效程序，具备解决问题的综合能力。</div>\n<div class=\"course-heading-intro_team\">&mdash;&mdash; 课程团队</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">课程概述</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>本课基于主讲教师在北京大学讲授数据结构与算法课（Python版）的多年教学实践经验，面向具有Python语言程序设计基础的大学生和社会公众，介绍常见的基本数据结构以及相关经典算法，强调问题-数据-算法的抽象过程，关注数据结构与算法的时间空间效率，培养学生编写出高效程序，从而解决实际问题的综合能力。</p>\n<p>&nbsp;</p>\n<p>本课内容包括课程概述、算法分析、基本线性结构、递归与动态规划、排序与查找、树及其算法、图及其算法等。课程将围绕着&ldquo;算法+数据结构=程序&rdquo;的思路，以问题求解为导向进行学习，帮助学生提高理论、抽象、设计的能力。</p>\n<p>&nbsp;</p>\n<p>本课注重数据结构与算法的实践与应用，在课程中穿插了生动案例和编程练习，引导学生积极建立数据抽象和层次分析的思维模式，通过解决实际问题来加深对数据组织结构和相应处理算法的学习体会，并学会通过实际应用情况来权衡时空和其它资源开销，达到最优的应用效果。</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">授课目标</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>本课注重数据结构与算法的实践与应用，在课程中穿插了生动案例和编程练习，引导学生积极建立数据抽象和层次分析的思维模式，通过解决实际问题来加深对数据组织结构和相应处理算法的学习体会，并学会通过实际应用情况来权衡时空和其它资源开销，达到最优的应用效果。</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">课程大纲</span></div>\n<div id=\"j-course-outline\" class=\"category-content j-cover-overflow\">\n<div class=\"outline\">\n<div class=\"f-richEditorText\">\n<p>一、概述</p>\n<p>101 引子：数据时代 9m53s</p>\n<p>102 问题求解的计算之道 12m20s</p>\n<p>103 图灵机计算模型 14m49s</p>\n<p>104 算法和计算复杂性 14m28s</p>\n<p>105 突破计算极限 9m08s</p>\n<p>106 什么是抽象和实现 9m00s</p>\n<p>107 为什么研究数据结构与算法 10m02s</p>\n<p>108 从C转换到Python</p>\n<p>第一周测验</p>\n<p>二、算法分析</p>\n<p>201 什么是算法分析 16m49s</p>\n<p>202 大O表示法 13m06s</p>\n<p>203 &ldquo;变位词&rdquo;判断问题（上） 10m27s</p>\n<p>204 &ldquo;变位词&rdquo;判断问题（下） 9m54s</p>\n<p>205 Python数据类型的性能（上） 12m37s</p>\n<p>206 Python数据类型的性能（下） 10m18s</p>\n<p>207 怎么做慕课Python在线评测OJ作业</p>\n<p>第二周测验</p>\n<p>OJ的适应性测试</p>\n<p>三、基本结构（上）</p>\n<p>301 什么是线性结构 6m59s</p>\n<p>302 栈抽象数据类型及Python实现 10m14s</p>\n<p>303 栈的应用：简单括号匹配 11m19s</p>\n<p>304 栈的应用：十进制转换为二进制 9m34s</p>\n<p>305 表达式转换（上） 11m24s</p>\n<p>306 表达式转换（下） 12m36s</p>\n<p>307 后缀表达式求值 8m18s</p>\n<p>第三周测验</p>\n<p>第三周作业</p>\n<p>四、基本结构（下）</p>\n<p>308 队列抽象数据类型及Python实现 10m01s</p>\n<p>309 队列的应用：热土豆 7m52s</p>\n<p>310 队列的应用：打印任务（上） 7m37s</p>\n<p>311 队列的应用：打印任务（下） 17m23s</p>\n<p>312 双端队列抽象数据类型及Python实现+回文词判定 7m25s</p>\n<p>313 无序表抽象数据类型及Python实现 11m03s</p>\n<p>314 无序表的链表实现 12m54s</p>\n<p>315 有序表抽象数据类型及Python实现 13m14s</p>\n<p>316 线性结构小结 6m08s</p>\n<p>第四周测验</p>\n<p>第四周作业（北大选课同学选做）</p>\n<p>五、递归（上）</p>\n<p>401 什么是递归 13m20s</p>\n<p>402 递归的应用：任意进制转换 6m32s</p>\n<p>403 递归调用的实现 8m32s</p>\n<p>404 递归可视化：分形树 10m54s</p>\n<p>405 递归可视化：谢尔宾斯基三角 10m31s</p>\n<p>406 递归的应用：汉诺塔 10m37s</p>\n<p>407 递归的应用：探索迷宫 16m35s</p>\n<p>第五周测验</p>\n<p>第五周作业（北大选课同学选做）</p>\n<p>六、递归（下）</p>\n<p>408 分治策略 5m34s</p>\n<p>409 优化问题和贪心策略 7m49s</p>\n<p>410 找零兑换问题的递归解法 12m32s</p>\n<p>411 找零兑换问题的动态规划解法 13m26s</p>\n<p>412 动态规划案例分析 17m53s</p>\n<p>413 递归小结 6m51s</p>\n<p>第六周测验</p>\n<p>第六周作业（北大选课同学选做）</p>\n<p>七、排序与查找（上）</p>\n<p>501 顺序查找算法及分析 9m41s</p>\n<p>502 二分查找算法及分析 12m20s</p>\n<p>503 冒泡和选择排序算法及分析 12m14s</p>\n<p>504 插入排序算法及分析 7m06s</p>\n<p>505 谢尔排序算法及分析 6m15s</p>\n<p>506 归并排序算法及分析 9m13s</p>\n<p>507 快速排序算法及分析 12m30s</p>\n<p>第七周测验</p>\n<p>第七周作业（北大选课同学选做）</p>\n<p>八、排序与查找（下）</p>\n<p>508 什么是散列 7m21s</p>\n<p>509 完美散列函数 15m02s</p>\n<p>510 区块链技术 17m20s</p>\n<p>511 散列函数设计 8m47s</p>\n<p>512 冲突解决方案 11m59s</p>\n<p>513 映射抽象数据类型及Python实现 14m58s</p>\n<p>514 排序与查找小结 9m45s</p>\n<p>第八周测验</p>\n<p>第八周作业（北大选课同学选做）</p>\n<p>九、树及算法（上）</p>\n<p>601 什么是树 8m37s</p>\n<p>602 树结构相关术语 8m23s</p>\n<p>603 树的嵌套列表实现 11m00s</p>\n<p>604 树的链表实现 6m57s</p>\n<p>605 树的应用：表达式解析（上） 13m03s</p>\n<p>606 树的应用：表达式解析（下） 15m15s</p>\n<p>607 树的遍历 10m11s</p>\n<p>608 优先队列和二叉堆 11m45s</p>\n<p>609 二叉堆的Python实现 13m14s</p>\n<p>第九周测验</p>\n<p>第九周作业（北大选课同学选做）</p>\n<p>十、树及算法（下）</p>\n<p>610 二叉查找树及操作 8m59s</p>\n<p>611 二叉查找树实现及算法分析（上） 11m55s</p>\n<p>612 二叉查找树实现及算法分析（下） 14m39s</p>\n<p>613 AVL树的定义和性能 7m08s</p>\n<p>614 AVL树的Python实现 20m04s</p>\n<p>615 树结构小结 7m39s</p>\n<p>第十周测验</p>\n<p>第十周作业（北大选课同学选做）</p>\n<p>十一、图及算法（上）</p>\n<p>701 图的基本概念及相关术语 11m51s</p>\n<p>702 图抽象数据类型 5m57s</p>\n<p>703 图抽象数据类型的Python实现 5m26s</p>\n<p>704 图的应用：词梯问题 8m20s</p>\n<p>705 实现广度优先搜索 15m07s</p>\n<p>706 图的应用：骑士周游问题 6m37s</p>\n<p>707 骑士周游问题算法实现 7m56s</p>\n<p>708 骑士周游问题算法分析与改进 8m45s</p>\n<p>第十一周测验</p>\n<p>第十一周作业（北大选课同学选做）</p>\n<p>十二、图及算法（下）</p>\n<p>709 通用的深度优先搜索 10m27s</p>\n<p>710 图的应用：拓扑排序 9m30s</p>\n<p>711 图的应用：强连通分支 11m16s</p>\n<p>712 图的应用：最短路径 16m54s</p>\n<p>713 图的应用：最小生成树 12m08s</p>\n<p>714 图结构小结 6m33s</p>\n<p>第十二周测验</p>\n<p>第十二周作业</p>\n</div>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">预备知识</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>具备基本的Python语言程序设计能力，比如学习过这门慕课：[Python语言基础与应用](<a href=\"https://www.icourse163.org/course/0809PKU036-1003479006)%E3%80%82\">https://www.icourse163.org/course/0809PKU036-1003479006</a>）</p>\n</div>\n</div>\n<div id=\"requirements\" class=\"category-title f-f0 certRope\">&nbsp;<span class=\"f-ib f-vam\">证书要求</span></div>\n<div class=\"category-content j-cover-overflow certRope\">\n<div class=\"f-richEditorText\">\n<ul class=\" list-paddingleft-2\">\n<li>\n<p>本课程采取百分制，单元测验和作业占45分，讨论区讨论占5分，期末在线考试占50分。</p>\n</li>\n<li>\n<p>60分-84分可获得合格证书，85分（含）以上可申请优秀证书。证书自愿申请。</p>\n</li>\n</ul>\n<p>&nbsp;</p>\n<p>注：本课程没有免费证书，只有认证证书。认证证书需付费在线申请，成绩达到课程考核分数标准的学生，具备申请资格。</p>\n<p>认证证书有电子版和纸质版。纸质版认证证书上含有二维码和证书编号，可以在线验证真伪。</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">参考资料</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<ul class=\" list-paddingleft-2\">\n<li>\n<p>【<a href=\"https://gis4g.pku.edu.cn/course/pythonds/\" target=\"_blank\" rel=\"noopener\">北京大学地空学院数据结构与算法B课程网站</a>】</p>\n</li>\n<li>\n<p>已出版纸质教材：Python数据结构与算法分析 第2版，[美] 布拉德利&middot;米勒（Bradley N.Miller），戴维&middot;拉努姆（David L.Ranum） 著，吕能，刁寿钧 译，人民邮电出版社，2019【<a href=\"https://item.jd.com/12570153.html\" target=\"_blank\" rel=\"noopener\">图书链接</a>】</p>\n</li>\n<li>\n<p>[数据结构与算法Python-中文版-内部教材](<a href=\"https://gis4g.pku.edu.cn/download/sessdsa-textbook/\">https://gis4g.pku.edu.cn/download/sessdsa-textbook/</a>&nbsp; )</p>\n</li>\n<li>\n<p>[在线英文版教材](<a href=\"https://runestone.academy/runestone/books/published/pythonds/index.html\">https://runestone.academy/runestone/books/published/pythonds/index.html</a>&nbsp; )</p>\n</li>\n<li>\n<p>[Python3官方中文版文档](<a href=\"https://docs.python.org/zh-cn/3/\">https://docs.python.org/zh-cn/3/</a>&nbsp; )</p>\n</li>\n<li>\n<p>[Python3烹饪书](<a href=\"https://python3-cookbook.readthedocs.io/zh_CN/latest/index.html\">https://python3-cookbook.readthedocs.io/zh_CN/latest/index.html</a>&nbsp; )</p>\n</li>\n<li>\n<p>[在线Python代码规范](<a href=\"https://python-guide.gitbooks.io/python-style-guide/content/\">https://python-guide.gitbooks.io/python-style-guide/content/</a>&nbsp; )</p>\n</li>\n</ul>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">常见问题</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>Q : &nbsp;学习这门课需要什么基础？<br />A : &nbsp;具备基本的Python语言程序设计能力即可，比如学习过这门慕课：[Python语言基础与应用](<a href=\"https://www.icourse163.org/course/0809PKU036-1003479006)%E3%80%82\">https://www.icourse163.org/course/0809PKU036-1003479006</a>）。</p>\n<p>&nbsp;</p>\n<p>Q : &nbsp;本课程教学使用的Python语言是哪个版本？</p>\n<p>A : &nbsp;Python 3.x，本课程代码示例均采用Python 3.x版本。虽然众所周知3.x版本与2.x版本并不兼容，但如今所有Python主流功能库都可以稳定运行在Python 3.x版本下，绝大多数Python程序员都已经使用Python 3.x版本。</p>\n<p>&nbsp;</p>\n<p>Q : &nbsp;通过Python语言学习数据结构与算法有什么优势呢？</p>\n<p>A : &nbsp;相比传统的C/C++/Java语言描述数据结构与算法，Python语言的抽象层次更高，代码描述更简洁，学习者可以集中注意力在数据的操作语义及其应用，更多关注用合适的数据结构和算法来进行问题求解，而不会被繁杂的代码描述细节所干扰。</p>\n</div>\n</div>', '2020-11-30 00:25:55', '2020-11-30 00:25:55');
INSERT INTO `edu_course_description` VALUES ('1333086446952816641', '<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">课程概述</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>该课程通过对VR知识体系的精心组织，即兼顾了VR技术的前沿性，又保证了VR知识点的系统性；即有原理知识的讲述，又与实际应用紧密结合。该课程将为学生构建较为完整的VR知识体系结构；使之掌握虚拟现实技术的理论内涵与应用外延。</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">授课目标</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>本课程从理论基础讲起，以应用场景过渡，落脚于使用UE进行实战。</p>\n<p>请大家循序渐进，从理论层面理解虚拟现实的原理，从设计的角度考虑创作的目标，使用工具完成创作。</p>\n<p>课程学习中有问题，请在答疑区发帖，老师们会尽快给大家回复。</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">课程大纲</span></div>\n<div id=\"j-course-outline\" class=\"category-content j-cover-overflow\">\n<div class=\"outline\">\n<div class=\"outline__new-outline\">\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">01</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">虚拟现实概述</span></div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">1.1 VR的概念及其三大特征</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">1.2 VR的概念性体系结构</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">1.3 VR的历史及现状</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">02</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">VR的虚拟环境构建方式之一----基于三维图形的虚拟环境构建</span></div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">2.1 虚拟环境的构建技术</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">2.2 虚拟现实引擎</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">2.3 虚拟现实网络规范语言</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">03</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">VR的虚拟环境构建方式之二----基于图像的虚拟环境构建</span></div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">3.1 基于图像的建模与绘制技术（IBMR）</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">3.2 全景图技术</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">3.3 其他IBMR技术</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">3.4 两种构建模式之比较</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">04</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">虚拟现实接口设备</span></div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">4.1 人的立体视觉特征</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">4.2 各类视觉感知设备</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">4.3 听觉、力觉/触觉、味觉/嗅觉等感知设备</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">4.4 跟踪设备</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">4.5 关于VR接口设备的总结与思考</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">05</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">基础图形技术</span></div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">5.0 基础图形技术</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">5.1 计算机图形学的概念与三大研究内容</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">5.2 图形流水线</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">5.3 基础图形库：OpenGL与Direct 3D</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">5.4 GPU的概念及其发展</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">5.5 基本渲染技术</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">5.6 加速渲染技术</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">5.7 场景管理----场景图与空间划分</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">5.8 碰撞检测</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">06</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">VR的应用</span></div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">6.1 VR与教育</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">6.2 VR与游戏</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">07</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">设计与实现</span></div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">7.1 UE基础教学</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">7.2 模型基本处理</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">7.3 材质编辑器</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">7.4 基础材质制作</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">7.5 混合纹理材质</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">7.6 基本光照</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">7.7 光照设计与制作</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">7.8 光照烘焙</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n<div id=\"requirements\" class=\"category-title f-f0 certRope\">&nbsp;<span class=\"f-ib f-vam\">证书要求</span></div>\n<div class=\"category-content j-cover-overflow certRope\">\n<div class=\"f-richEditorText\">\n<h4>60分合格，90分优秀。</h4>\n<p>为了保障证书权威性，平台不再支持免费电子证书，只提供证书认证。</p>\n<p>认证证书需申请，每份100元。</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">参考资料</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>《虚拟现实导论&mdash;&mdash;原理与实践》黄心渊、杨刚等，高等教育出版社，2018</p>\n</div>\n</div>', '2020-11-30 00:32:31', '2020-11-30 00:32:31');
INSERT INTO `edu_course_description` VALUES ('1333088371052027906', '<div id=\"j-course-heading-intro\" class=\"course-heading-intro\">\n<div id=\"j-rectxt2\" class=\"course-heading-intro_intro\">数据库及相关技术是计算机应用中一个发展迅速、应用广泛的领域。本课程将由5位一线教师带领大家学习关系数据库的基本概念及原理、理论模型和规范化设计方法、关系数据库语言SQL和数据库管理和运维等内容。课程面向计算机技术应用型人才培养需要，具有学习门槛低、理论联系实际、实用性较强等特点。 通过学习，学生将能够熟练运用SQL语言构建业务所需的单表、跨表、分组等查询语句；结合系统开发需要，能够选择合适的数据库系统建模、开发和管理工具，通过E-R图设计数据库，使用范式理论分析和评价关系模式；学习课程内容后可以为学生从事大数据技术开发、研发、数据挖掘序、人工智能等课程的学习奠定基础。 经过多次开课，本课程积累了大量理论教学内容、实践操作视频、数据库应用案例以及完整的课程自学、混合式教学资料、辅助学习资料等。结合每轮学生反馈和技术发展情况，课程团队持续改进和完善教学内容。欢迎大家加入我们一起玩转数据。</div>\n<div class=\"course-heading-intro_team\">&mdash;&mdash; 课程团队</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">课程概述</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p><strong>【为什么要学习这门课？】</strong>课程学习原因：数据库及其相关技术是计算机应用中一个非常活跃、发展迅速、应用广泛的领域。随着物联网、移动互联网、社交媒体等信息技术的飞速发展，数据资源急剧膨胀，如何解决数据管理的相关理论和技术问题，并利用计算机对这些数据资源进行科学地组织、存储、检索、维护和共享，是数据库原理与应用课程的主要教学内容。</p>\n<p><img id=\"1\" src=\"https://edu-image.nosdn.127.net/E7669AB4257E25EF1764311A0F04DCD7.png?imageView&amp;thumbnail=890x0&amp;quality=100\" /></p>\n<p><strong>【这门课的主题是关于什么】</strong>课程讲授内容：本课程的教学团队由一线教师组成，几位老师将先后带领同学们学习关系数据库系统的基本概念和原理、关系数据模型、SQL语言、数据库管理、关系数据库设计以及数据库高级知识等内容。</p>\n<p><img id=\"1\" src=\"https://edu-image.nosdn.127.net/D933EFD1BAF29F0CE97CE02FDE124E7C.png?imageView&amp;thumbnail=890x0&amp;quality=100\" /></p>\n<p><strong>【学习这门课可以获得什么？特别是对自己有什么帮助和应用】帮助和应用：</strong>通过本课程的学习，学生将掌握关系数据库基本概念和原理，能够通过SQL语句管理数据库并进行简单和复杂的数据查询操作，具备针对各类复杂业务需求进行规范化关系数据库设计的能力，具有开展高级数据库管理、大数据分析和挖掘等工作的基本素质。</p>\n<p><img id=\"1\" src=\"https://edu-image.nosdn.127.net/26795806E3FE21BB069AAB9EB8FE8FB4.png?imageView&amp;thumbnail=890x0&amp;quality=100\" /></p>\n<p><strong>【这门课有什么特色和亮点】特色亮点：</strong>本课程既能够满足计算机专业数据库原理的教学任务，也能够满足非计算机专业数据库应用教学要求。课程提供了丰富的教学指导、学习课件、实践操作、实验指导、习题指导以及数据库案例分析，能够对以数据库应用型人才培养为目标的混合式教学提供所需的线上资源。同时，本课程还即将面向社会数据库学习爱好者需要，在SQL Server基础上，持续提供MySQL的配套辅导，覆盖主流数据库管理系统的应用场景。</p>\n<p><img id=\"1\" src=\"https://edu-image.nosdn.127.net/03655C01D05DC4B9BB25376C3A0CD3E9.png?imageView&amp;thumbnail=890x0&amp;quality=100\" /></p>\n<p>同学们，这是一门理论联系实际、实用性非常强的计算机及相关专业的核心骨干课。请加入我们，一起玩转数据，为今后开展数据管理及系统开发等相关工作奠定基础，也为今后学习数据仓库、数据挖掘、大数据分析处理等课程做好准备。</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">授课目标</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>面向计算机技术应用型人才培养目标，结合OBE（基于学习产出的教学模式）理论及课程在计算机专业教学支撑的毕业要求，本课程的授课目标为：</p>\n<p>&nbsp;</p>\n<p><strong>1. 工程知识方面：</strong></p>\n<p class=\"p1\">【毕业要求】</p>\n<p class=\"p1\">能够将数学、自然科学、工程基础和计算机专业知识用于对计算机应用领域复杂工程问题解决方案的分析和改进。</p>\n<p class=\"p1\">【课程目标】</p>\n<p class=\"p1\">能够理解数据库基本概念和原理，数据安全性和完整性的含义，事务的特性，大数据的特征，数据库故障类型并掌握相应的恢复策略，掌握SQL语言的语法，索引和视图的用途，并发操作对数据不一致的影响和解决办法，深刻理解关系数据库规范化的相关概念并可对关系模式进行规范化分析，掌握Transact-SQL语法。</p>\n<p class=\"p1\">&nbsp;</p>\n<p class=\"p1\">2. 问题分析方面：</p>\n<p class=\"p1\">【毕业要求】</p>\n<p class=\"p1\">能够运用应用数学、自然科学和计算机专业的知识和方法，针对可行方案进行推理和验证，获得合理有效的解决方案。</p>\n<p class=\"p1\">【课程目标】</p>\n<p class=\"p1\">针对数据库系统的数据查询需要，能够使用关系代数和SQL语言构建所需的查询语句。针对数据库系统的数据处理需要，能够运用SQL语言实现数据库、数据表的创建、修改、删除操作，能够使用Transact-SQL设计和建立存储过程和自定义函数，能够使构建满足需要的数据库触发器。针对数据库系统的数据管理需要，能够进行数据库的安全管理以及数据库备份和恢复等操作。</p>\n<p class=\"p1\">&nbsp;</p>\n<p class=\"p1\">&nbsp;</p>\n<p class=\"p1\">3. 设计/开发解决方案方面：</p>\n<p class=\"p1\">【毕业要求】</p>\n<p class=\"p1\">能够针对特定需求完成系统设计、模块的软件设计和硬件设计。</p>\n<p class=\"p1\">【课程目标】</p>\n<p class=\"p1\">根据实际数据库系统设计与实现中各类业务的数据定义、数据查询和数据操纵要求，能够综合运用单关系查询、多关系查询、分组查询、子查询和连接等查询设计、实现和调试具有多层嵌套、复杂连接关系协同的SQL语句。根据数据库系统数据库结构设计要求，具有分析数据库需求、抽象客观业务流程相关的实体和实体关系、通过E-R图设计数据库、转换E-R图为关系模式、使用范式理论分析和评价关系模式、优化库表结构和存储结构的能力。</p>\n<p class=\"p1\">&nbsp;</p>\n<p class=\"p1\">&nbsp;</p>\n<p class=\"p1\">4. 使用现代工具方面：</p>\n<p class=\"p1\">【毕业要求】</p>\n<p class=\"p1\">能够开发、选择与使用恰当的计算机系统的应用环境与开发工具，对复杂工程问题进行分析与设计。</p>\n<p class=\"p1\">【课程目标】</p>\n<p class=\"p1\">针对实际数据库系统分析、设计、开发和管理需要，能够选择合适的数据库系统建模、开发和管理工具，搭建和配置相应环境，开展数据库的分析、设计、搭建、处理和维护等工作。</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">课程大纲</span></div>\n<div id=\"j-course-outline\" class=\"category-content j-cover-overflow\">\n<div class=\"outline\">\n<div class=\"outline__new-outline\">\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">01</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">数据库系统概述</span></div>\n<div class=\"outline__new-outline__chapter__content__goals\">【学习目标】（1）能够理解信息、数据与数据处理的正确含义，数据管理技术发展的3个阶段的特点，数据库系统的组成和结构，数据库系统常见的外部体系结构，三个世界的相关概念的对应关系，层次模型和网状模型表示数据的结构特点以及主要问题，关系和面向对象的模型的含义和特点，大数据技术的性质，我国典型的自主知识产权数据库。（2）能够掌握数据库模式的关系数据库的含义，从客观世界到计算机世界的数据抽象过程，能够分析数据库管理系统的三级模式、二级映像（二级映射）的原理以及所带来的逻辑独立性和物理独立性等优势，能够准确判断客观世界中常见实体之间的关系类型。</div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【教学安排】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第1讲： 数据库课程概述及数据库相关概念（课程的目标；课程的主要授课内容；数据库课程与其他课程的关系；数据和信息；数据库；数据库管理系统；数据库系统）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第2讲：数据库技术的产生与发展（数据管理技术的产生和发展）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第3讲：数据库系统组成（数据库系统的组成要素）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第4讲：数据库的内部和外部体系结构（数据库系统的体系结构；数据库系统的内部体系结构；数据库系统模式的概念；数据库系统的三级模式结构；数据库的二级映像功能与数据独立性；数据库系统的外部体系结构）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第5讲：三个世界及有关概念（数据抽象的三个世界，三个世界涉及的各类数据库概念）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第6讲：数据模型（数据模型的组成要素；概念模型；层次模型；网状模型；关系模型；面向对象模型）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">&nbsp;</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【知识点和重难点】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">知识点：数据库的发展历史、三个世界的主要概念、具有数据库的软件系统的架构、数据库系统确保数据逻辑独立性和物理独立性的方法、如何将客观世界的内容转换为计算机世界中的数据、数据在数据库的组织模型等内容。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">重点：掌握数据库的三层模式和二级映像、数据库系统中数据库与数据库管理系统之间的关系、三个世界中各类概念的关系以及不同数据模型的优点和缺点。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">难点：数据库的物理独立性和逻辑独立性。</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">02</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">关系数据库模型</span></div>\n<div class=\"outline__new-outline__chapter__content__goals\">【学习目标】（1）能够理解域、笛卡尔积的概念，能够掌握关系的性质以及规范化关系的要求，能够理解关系模式、关系数据库和关系数据库模式等概念之间的关系，能够理解&theta;连接、等值连接和自然连接的关系。（2）能够根据候选码、外码的定义和内涵识别出常见关系的候选码，能够深刻理解实体完整性和参照完整性与主码和外码的关系，并能够分析和验证常见关系是否满足实体完整性和参照完整性要求，掌握一些常见场景中用户自定义完整性的表述方法，能够运用元组关系演算和域关系演算实现单关系和多关系间的简单查询任务。（3）能够在多关系上熟练运用关系代数中的传统集合运算、专门的关系运算（选择，投影，连接和除法）实现各类简单和复杂的查询任务。</div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【教学安排】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第1讲 关系模型的数据结构及其形式化定义（关系的形式化定义；关系的性质；关系模式；关系数据库模式）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第2讲 关系的码和关系的完整性（候选码和主码；关系的实体完整性；关系参照完整性；关系的用户自定义完整性）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第3讲 关系代数（关系代数的分类及运算符；传统的集合运算；专门的关系运算）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第4讲 关系演算（元组关系演算；域关系演算）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【辅助教程（前序知识与学习指导）】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">1. 集合的相关概念；</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">2. 笛卡尔积和关系</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【知识点和重难点】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">知识点：讲授关系数据库的数学基础、关系数据库如何确保与客观世界内容相互一致的方法、关系数据库的操作方法（SQL语句的产生基础）等。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">重点：掌握关系的性质、关系数据库中完整性与主码及外码的关系、使用关系代数操作数据库的方法、使用关系演算操作数据库的方法。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">难点：关系的连接操作、关系代数中除法运算以及元组关系演算。</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">03</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">关系数据库标准语言-SQL语言</span></div>\n<div class=\"outline__new-outline__chapter__content__goals\">【学习目标】（1）能够了解有关SQL语言的发展特点及标准化过程，理解SQL语言的非过程化特点以及SQL语言涉及的主要概念，能够在各类操作系统中搭建SQL Server的开发环境并能够了解SQL Server安装后提供的系统数据库。（2）能够使用SQL Server的关键组件SQL Server Management Studio进行数据库、数据表以及数据的增、删、改、查等操作，能够通过SQL Server Management Studio创建、查看和修改索引和约束。（3）能够理解索引的用途以及索引粒度对数据库系统的影响，能够针对不同应用场景，选择合适的数据类型和数据精度对其刻画和描述，能够明确视图的应用范围和存储特点。能够理解子查询和相关子查询的运行差异。 （4）能够针对软件工程中数据库物理设计结果，熟练的运用SQL语句进行数据数据库的创建和删除、带约束条件的数据表的创建、修改和删除。能够综合运用条件查询、分组查询、常用库函数、子查询、查询排序、内外连接查询、合并查询等实现各类业务应用系统的数据处理任务。能够根据业务需要，运用SQL语句创建、查询和删除单表和多表基础上的视图。能够掌握通过已有数据表中数据创建数据表的方法。</div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【教学安排】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第1讲 SQL概述（SQL标准化过程；SQL基本概念；SQL特点；SQL Server介绍）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第2讲 数据库的结构、创建和使用（数据库的结构；SQL Server数据库；数据库的创建；数据库的修改；数据库的删除；查看数据库信息）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第3讲 数据类型（数据类型）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第4讲 数据表的创建和使用（创建数据表；定义数据表约束；修改数据表；删除数据表；查看数据表）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第5讲 单关系（表）的数据查询。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第6讲 多关系（表）的数据查询。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第7讲 子查询（普通子查询和相关子查询）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第8讲 其他类型查询。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第9讲 数据表中数据的操纵（修改数据；添加数据；删除数据）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第10讲 视图（视图的作用；创建视图；修改视图；删除视图；查询视图；更新视图）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第11讲 创建与使用索引（索引概述；索引类型；索引设计；索引使用和优化）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【辅助教程】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">1.使用虚拟机安装Windows</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">2.SQL Server 安装教程上</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">3.SQL Server 安装教程下</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">4. MySQL安装</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">5. MySQL管理工具的选择和使用</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【实验指导】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">实验3.1 SQL Server 2012的安装和使用</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">实验3.2 设计数据库、创建数据库和数据表</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">实验3.3 数据库的单表查询和连接查询</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">实验3.4 数据库查询和数据操纵</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">实验3.5 视图</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【知识点和重难点】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">知识点：讲授SQL语言有关数据库和数据表的创建、修改、删除等操作语法和案例，讲授使用SQL语句进行数据表中数据查询、修改、删除的语法和案例，讲授数据库视图和索引等概念和操作方法。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">重点：掌握SQL语句创建数据库、创建数据表结构、修改数据表结构、删除数据表、数据表连接查询、子查询、视图的用途以及索引分类等内容。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">难点：SQL连接查询、SQL子查询和分组查询等。</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">04</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">关系数据库的规范化</span></div>\n<div class=\"outline__new-outline__chapter__content__goals\">【学习目标】（1）能够理解不规范关系带来的问题以及规范化问题的提出原因，能够理解函数依赖的含义并可以分析和提取常见关系模式上的函数依赖关系。（2）能够掌握Armstrong推理规则和推论的含义并能够根据函数依赖定义证明推理规则的正确性。能够掌握运用函数依赖推理规则从已知的函数依赖集中推理所有函数依赖的方法。（3）能够深刻理解属性集闭包的含义和应用场景，并可熟练的求解任意关系上的属性集闭包。能够根据给定的函数依赖集对属性进行归类，然后使用候选码求解算法，求解给定关系上的候选码。理解函数依赖集逻辑等价的含义并可使用最小函数依赖集求解步骤求解给定函数依赖集的最小函数依赖集。（4）能够理解关系模式分析的目标和意义，并能够分析和验证给定关系模式的分解结果是否保持函数依赖并具有无损连接性。（5）能够理解第一范式、第二范式、第三范式和BC范式的定义和对关系规范化约束的递进关系，并能够分析和验证给定关系模式属于哪一级范式。（6）能够熟练使用关系的规范化步骤，对抽象后的关系模式进行指定范式级别规范化操作。掌握如何将给定的关系模式分解为保持函数依赖且具有无损连接的第三范式和BC范式的方法。</div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【教学安排】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第1讲 规范化问题的提出</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第2讲 函数依赖（定义；逻辑蕴含；函数依赖的推理规则；完全函数依赖；部分函数依赖；传递函数依赖；属性集闭包算法；候选码求解算法；函数依赖集的等价性；最小函数依赖）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第3讲 关系模式的分解（问题概述；无损连接分解；无损分解测试算法；保持函数依赖的分解）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第4讲 关系模式的范式（第一范式；第二范式；第三范式；BC范式）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第5讲 关系模式的规范化（关系模式规范化的步骤；关系模式规范化的要求）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【辅助教程】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">规范化问题的理解</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【知识点和重难点】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">知识点：讲授关系数据规范化的必要性、函数依赖定义和特点、关系模式的分解方法、关系规范化范式等级。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">重点：掌握使用函数依赖计算属性集闭包、候选码和最小函数依赖集的方法，掌握3NF无损连接和满足函数依赖的转化方法等。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">难点：验证抽象后的关系模式属于范式的级别和通过范式的方法抽象数据库模式。</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">05</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">数据库安全和管理</span></div>\n<div class=\"outline__new-outline__chapter__content__goals\">【教学目标】（1）能够理解数据库安全性的内涵和外延以及数据隐私安全在大数据时代的意义，了解有关安全性控制常见策略，理解SQL Server控制数据安全性的3级机制以及登录账号和数据库账号在安全机制中的作用和映射关系。能够理解登录账号和数据库账号的常见角色以及数据库权限授予和回收的方法。能够熟练使用SQL Server Management Studio进行登录账号、用户账号设置的创建、角色和权限分配以及删除的方法。（2）能够理解数据库完整性的含义，使用形式化方式描述完整性规则的方法。能够理解常见完整性约束的分类，并掌握通过规则和默认的创建及绑定实现数据库完整性的方法。（3）能够理解数据库并发的内涵、事务的定义以及事务的4个主要特性。能够分析数据并发操作带来的数据不一致问题的根源,理解写锁和读锁对数据读写并发操作的影响，并能够熟练掌握使用三级封锁协议解决数据不一致性的方法。理解数据库死锁产生的原因、常见分析方法以及解决数据库死锁的常见办法。（4）能够理解数据库恢复的含义以及日志和转储在数据库恢复中的作用，能够识别三类常见的数据库故障并掌握使用日志和转储进行数据库故障恢复的策略和步骤。</div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【教学安排】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第1讲 数据库的安全性（数据库安全性含义；安全性控制一般方法；SQL Server安全性机制；SQL Server身份验证模式；SQL Server登录账号；SQL Server数据库账号）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第2讲 数据完整性控制（数据完整性含义；完整性规则的组成；完整性约束条件的分类；数据库完整性的实施；规则；默认）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第3讲 并发性控制与封锁（数据库并发性含义；事务性质；并发操作与数据不一致性；封锁）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第4讲 数据库的恢复（数据库恢复的含义；数据库恢复及其实现技术；数据库的故障和恢复的策略）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【辅助教程】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">1. My SQL的安全配置</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">2. My SQL的数据备份和恢复</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【实验指导】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">实验5.1 SQL Server数据库安全配置</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">实验5.2 SQL Server数据库系统的备份和恢复</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【知识点和重难点】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">知识点：讲授保护数据安全性的方法、数据库完整性控制手段-规则和默认、数据库并发操作导致的问题以及封锁协议、数据库故障的类型以及恢复方法。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">重点：使用SQL Server登录账号和数据库账号两级保障数据库的方法、数据库规则和默认的主要用途、使用读锁和写锁实现三级封锁协议的方法、数据库恢复的基础。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">难点：SQL Server登录账号和数据库账号的关系、SQL Server登录账号和数据库账号的权限管理、规则和默认在删除时候注意的事项、三级封锁协议。</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">06</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">关系数据库的设计</span></div>\n<div class=\"outline__new-outline__chapter__content__goals\">【学习目标】（1）能够理解数据库设计的主要工作和内容，理解自顶向下和自底向上方法论在数据库设计中的内涵，掌握数据库设计的主要步骤，了解华人科学家在数据库设计领域中的贡献和作用。（2）能够理解数据驱动的数据库需求分析方法和E-R图的组成要素，掌握在需求分析基础上，通过E-R图分析和抽象概念模型的方法，掌握在中大型数据库设计过程中，局部E-R图集成为全局E-R图时遇到冲突的解决方法。（3）能够熟练将E-R图转换为关系模式，并能够运用数据库规范化理论分析关系模式的范式级别。能够结合需求分析的对照结果、范式分析结果以及性能评价结果，对关系模式进行优化和改进。（4）能够理解数据库物理设计的主要任务以及关键步骤，物理结构评价和优化的方法。能够理解数据库实施、运行和维护的主要任务。</div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【教学安排】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第1讲 数据库设计概述（数据库设计任务；数据库设计方法；数据库设计步骤；华人科学家在数据库设计领域中的贡献）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第2讲 系统需求分析（需求分析的任务；需求分析的方法）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第3讲 概念结构设计（概念结构设计的必要性；概念模型的特点；概念模型的E-R表示方法；概念结构设计的方法和步骤）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第4讲 逻辑结构设计（逻辑结构设计的任务和步骤；关系模式的规范化；模式的评价和改进）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第5讲 物理结构设计（物理结构设计的内容；评价物理结构）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第6讲 数据库实施与维护（数据库实施的重点；数据库运行维护的重点）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【辅助教程】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">使用PowerDesingner进行数据库设计</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【实验指导】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">实验6.1 数据库系统的概要设计</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">实验6.2 数据库系统的逻辑设计</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">实验6.3 数据库系统的物理设计</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【知识点和重难点】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">知识点：讲授数据库设计的关键步骤、概念设计过程和使用工具、逻辑设计过程、物理设计过程、数据库实施和运维过程。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">重点：概念设计中E-R图的使用、各阶段设计的侧重点、概念设计和逻辑设计的转换方法。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">难点：客观世界问题到E-R模型的抽象过程、概念设计到逻辑设计的转化方法。</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"outline__new-outline__chapter f-cb\">\n<div class=\"outline__new-outline__chapter__number f-fl\">07</div>\n<div class=\"outline__new-outline__chapter__content f-fl\">\n<div class=\"outline__new-outline__chapter__content__title\"><span class=\"outline__new-outline__chapter__content__title__text\">SQL Server 高级应用</span></div>\n<div class=\"outline__new-outline__chapter__content__goals\">【学习目标】（1）能够掌握Transact-SQL程序设计中语法结构、常用命令、常用函数以及用户自定义函数的区别，能够根据数据处理需要，通过Transact-SQL编写简单的用户自定义函数。（2）能够根据实际数据处理需要，进行简单存储过程创建、查看、修改、删除操作。理解触发器与存储过程的关系、触发器的原理以及触发器的常见应用场景。（3）能够理解数据库备份和还原的原理，能够掌握使用转储和脚本两种数据库备份和还原的方法。</div>\n<div class=\"outline__new-outline__chapter__content__plan\">\n<div class=\"outline__new-outline__chapter__content__plan__title\">课时</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons\">\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【教学安排】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第1讲 Transact-SQL程序设计（变量、批处理、流程控制命令、常用命令和常用函数）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第2讲 存储过程（存储过程的概念；创建存储过程；查看存储过程；执行存储过程；修改存储过程）</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第3讲 触发器（触发器概述；触发器工作原理；创建触发器；查看触发器；修改触发器）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">第4讲 备份和还原（数据库备份和还原概述；备份数据库的方法；还原数据库的方法）。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【辅助教程】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">1. MySQL存储过程设计</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">2. MySQL触发器设计</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【实验指导】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">实验7.1 T-SQL程序设计</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">实验7.2 存储过程设计</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">实验7.3 触发器设计</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">【知识点和重难点】</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">知识点：讲授T-SQL的基本语法、自定义函数类型和方法、存储过程的语法结构和案例、触发器的原理和用途、数据库备份和还原的方法。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">重点：自定义函数的编写、存储过程的编写、触发器的应用场景选择、数据库备份和还原的主要手段及注意事项。</div>\n<div class=\"outline__new-outline__chapter__content__plan__lessons__lesson\">难点：表值函数的编写、存储过程的编写。</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">预备知识</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p><strong>对于社会从业人员、感兴趣的同学、继续教育同学、选修在线视频课的同学：</strong></p>\n<p>-只需具备基础的计算机知识。</p>\n<p>&nbsp;</p>\n<p><strong>对于选修《数据库系统》、《数据库原理与应用》、《数据库应用》等课程混合式教学的同学：</strong></p>\n<p>-理解离散数学中笛卡尔积、关系、集合基本运算等内容。</p>\n<p>-了解数据结构中线性结构和树状数据结构。</p>\n<p>-了解操作系统等系统软件在计算机体系结构中的地位。</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div id=\"requirements\" class=\"category-title f-f0 certRope\">&nbsp;<span class=\"f-ib f-vam\">证书要求</span></div>\n<div class=\"category-content j-cover-overflow certRope\">\n<div class=\"f-richEditorText\">\n<p>总分的组成：课堂讨论占10%；单元测试占20%；单元作业占10%；考试占60%。</p>\n<p>&nbsp;</p>\n<p>证书要求：</p>\n<p>-60分至80分（不含80）将授予合格证书。</p>\n<p>-80（包含80）分以上至100分将授予优秀证书。</p>\n<p>&nbsp;</p>\n<p>注：为保障证书权威性，平台取消免费证书，证书需要自费。</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">参考资料</span></div>\n<div class=\"category-content j-cover-overflow\">\n<div class=\"f-richEditorText\">\n<p>本课程配套教材为人民邮电出版社出版的<strong>《数据库原理及应用教程》（第4版|微课版</strong>）。<a href=\"https://www.ryjiaoyu.com/book/details/8217\" target=\"_blank\" rel=\"noopener\">书籍官方网站</a>：https://www.ryjiaoyu.com/book/details/8217。</p>\n<p>在第4版教材中，我们加入了诸多视频讲解的二维码，同学可扫描书上的二维码，随时随地进行学习。</p>\n<p>提供全书选择题、填空题、简答题、计算题和查询题的答案。</p>\n<p>提供教材相关的SQL Server实例，方便直接上手学习。</p>\n<p>本教材曾经获得&ldquo;十二五&rdquo;普通高等教育本科国家级规划教材和普通高等教育&ldquo;十一五&rdquo;国家级规划教材。</p>\n<p>如果同学们希望更加全面、系统的掌握本课程内容，可线下购买该书籍。</p>\n<p><img id=\"1\" src=\"https://edu-image.nosdn.127.net/EA129FACCBE0C27B5B6FDC9E791900DC.png?imageView&amp;thumbnail=890x0&amp;quality=100\" /></p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"category-title f-f0\">&nbsp;<span class=\"f-ib f-vam\">常见问题</span></div>\n<div class=\"category-content j-cover-overflow cover\">\n<div class=\"f-richEditorText\">\n<p>Q : &nbsp;和上一次发布的课程相比，本次课程主要有哪些变化？</p>\n<p>A :&nbsp;&nbsp;针对学生评价、教学反馈以及参加教学会议讨论结果，本课程在课程材料、实践教学、课程适用性等角度进行了诸多改进。首先，从多个角度丰富授课资料，主要包括：自学指导、混合教学指导、实验指导、先续知识辅助教程等；其次，针对习题和作业后续查阅困难以及缺少讲解等问题，我们重新录制了所有作业和习题的讲解，方便大家随时反馈复习；再次，部分学生使用的是MySQL数据库管理系统，为此，在与DBMS相关的部分，我们补充了与MySQL相关的教程，并对其他出现的内容在MySQL上进行了兼容性测试，确保课程内容的适用性。最后，结合课程思政需要，围绕数据库自主知识产权、数据库职业道德和数据库隐私保护等方面提供了辅助阅读材料。</p>\n<p>&nbsp;</p>\n<p>Q : &nbsp;作为其他学校的教师，是否可以使用该课程的开展混合式教学，是否方便获取学生课程学习信息？</p>\n<p>A :&nbsp; 授课团队一直秉承开放授课心态，十分欢迎其他高校老师使用我们的课程资源。如果是教师，联系我们会提供更加丰富的混合式教学资源，我们也会积极根据老师的要求，选择大家共性需求，进一步开发相关的资源，提高课程的适用范围，欢迎大家提出建议。同时，由于平台和学校的限制，其他高校教师可能无法使用慕课堂等混合式教学环境，但是又急需开展混合式教学，需要获取学生在线学习数据，老师们可以联系我们，经过协商，我们可以为大家建立慕课堂，定期给大家导出所需学生在线学习信息，方便其他高校老师掌握学生在线学习情况。此外，我们十分欢迎其他学校老师一同参与到课程的建设过程中，相互交流，取长补短，互相进步，实现课程资源的共享，提升课程质量和水平。</p>\n<p>&nbsp;</p>\n<p>Q : &nbsp;我对数据库的相关知识十分感兴趣，是否要全部学完课程内容才能掌握所需知识？</p>\n<p>A :&nbsp;&nbsp;和以往课堂教学相比，在线课程往往以知识点形式呈现知识。为确保本课程服务的混合式教学工作可以与学校课堂内容对接，我们提供了相对全面的数据库原理和应用的知识。在实际学习中，同学可根据自身特点，有选择性的观看课程内容和实际操作视频，以便更加精准、高效的利用碎片化时间掌握所需内容。同时，大部分同学可能不了解不同学习目标定位对知识点的需求，为此，授课教师专门针对不同学习者的目标，梳理了知识脉络图，同学可根据知识脉络图选择相关知识点进行按需学习。对于其他内容，在学有余力或者其他时间也可以复习和补充，以充分发挥在线课程的特点。</p>\n<p>&nbsp;</p>\n<p>Q : &nbsp;这门课程是否需要具备较强的专业背景？</p>\n<p>A :&nbsp;&nbsp;这门课程既是计算机专业的骨干课程，也是相关专业的专业选修课程，同时，也可以作为从事计算机软件开发、数据分析、软件项目管理、人工智能等领域工作的先修课程。课程在设置之初，就考虑到选课学生的专业背景，同时考虑到各类工作对数据应用的需求，所以课程既提供了必要的原理、概念、模型，帮助学生建立数据管理思维，另一方面提供数据库设计、数据库应用设计、数据库安全管理等方面的实际应用内容。</p>\n<p>&nbsp;</p>\n<p>Q : &nbsp;这门课程使用哪个数据库管理系统？</p>\n<p>A :&nbsp;&nbsp;本课程推荐使用的数据库管理系统为SQL Server。SQL Server属于实际开发中应用较为广泛的数据库管理系统，特别是在诸多企业级、复杂级应用中，都使用了SQL Server。虽然课程使用SQL Server作为主要的数据库管理系统，但所教授的关于数据库的创建、修改、删除以及数据表的创建、修改、删除和查询等方面的SQL语句均为标准SQL，在Oracle、MySQL等流行的数据库管理系统中同样可以使用。此外，课程教授的SQL Server相关概念在流行的数据库管理系统中也同样具备。日后，即使开发项目所使用的不是SQL Server数据库，学生仍然可以通过本次课程内容，快速掌握如何操作项目使用的数据库管理系统。</p>\n<p>&nbsp;</p>\n<p>Q : &nbsp;如果我的操作系统是Linux，不是Windows操作系统，该如何安装SQL Server？</p>\n<p>A :&nbsp;&nbsp;本课程建议学生通过虚拟机的方式安装SQL Server。关于虚拟机安装SQL Server的教程，我们会在课程中提供，便于同学在任何环境中安装和使用SQL Server。</p>\n<p>&nbsp;</p>\n<p>Q : &nbsp;这门课程对从业人员是否有帮助？是否涉及到实践开发中的SQL语句、数据库编程、数据库设计等内容？</p>\n<p>A :&nbsp;&nbsp;&ldquo;数据库原理与应用&rdquo;是一门理论和实际结合的课程，本课程涵盖了数据库经典教学中概念和原理，同时，也系统的讲解了SQL标准语句、数据库的存储过程编程以及数据库E-R图的设计方法，对开展实践、从事与数据库相关开发工作的从业人员也有一定的参考意义。</p>\n<p>&nbsp;</p>\n<p>Q : &nbsp;数据库原理是否很抽象呢？学习门槛是否很高？</p>\n<p>A :&nbsp;&nbsp;和计算机类的其他课程相比，数据库是一门门槛较低的核心课程，在很多非计算机专业也是核心课程。数据库原理是提升实践能力以及数据库设计水平的重要基础。为了降低数据库抽象内容的上手难度，在本次学习中，我们将会提供一些必要的辅助材料。帮助同学理解抽象的数据库原理，将抽象的原理转换为易懂的知识，进而提升同学学习效果。</p>\n<p>&nbsp;</p>\n<p>Q : &nbsp;部分同学评价课程内容比较困难，老师也是照着ppt读，缺少互动，是否会影响我们学习？</p>\n<p>A :&nbsp;&nbsp;我们很感谢同学对本课程以往开课所提出的评价和建议。面对每一份评价，授课教师都在认真总结，并长期改进和完善课程内容，一方面，我们修改了原有课程中的内容，确保内容的准确性。另一方面，我们开展混合式教学，真正让课程内容动起来，并且活起来，老师的交流也会更加频繁。此外，为了弥补传统理论教学和ppt教学在互动方面的不足，我们提供了多种配套的实践学习内容，如录屏幕、实验等，同时，也借助教材平台，提供大量辅助练习和材料。感谢同学的关注，我们将会持续改进课程内容。</p>\n</div>\n<div class=\"cover-overflow-wrapper\">&nbsp;</div>\n</div>', '2020-11-30 00:40:10', '2020-11-30 00:40:10');

-- ----------------------------
-- Table structure for edu_subject
-- ----------------------------
DROP TABLE IF EXISTS `edu_subject`;
CREATE TABLE `edu_subject`  (
  `id` char(19) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '课程类别ID',
  `title` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '类别名称',
  `parent_id` char(19) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '0' COMMENT '父ID',
  `sort` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '排序字段',
  `gmt_create` datetime(0) NOT NULL COMMENT '创建时间',
  `gmt_modified` datetime(0) NOT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_parent_id`(`parent_id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '课程科目' ROW_FORMAT = COMPACT;

