-- ----------------------------
-- Table structure for edu_teacher
-- ----------------------------
DROP TABLE IF EXISTS `edu_teacher`;
CREATE TABLE `edu_teacher`  (
  `id` char(19) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '讲师ID',
  `name` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '讲师姓名',
  `intro` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '讲师简介',
  `career` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '讲师资历,一句话说明讲师',
  `level` int(10) UNSIGNED NOT NULL COMMENT '头衔 1高级讲师 2首席讲师',
  `avatar` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '讲师头像',
  `sort` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '排序',
  `is_deleted` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '逻辑删除 1（true）已删除， 0（false）未删除',
  `gmt_create` datetime(0) NOT NULL COMMENT '创建时间',
  `gmt_modified` datetime(0) NOT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `uk_name`(`name`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '讲师' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of edu_teacher
-- ----------------------------
INSERT INTO `edu_teacher` VALUES ('1327071752955412482', '徐航', '研究领域:多目标进化算法、物联网技术', '厦门大学计算机科学与技术博士', 1, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/11/29/f7d90819508c4402a7d233c3aa77f8d6file.png', 1, 0, '2020-11-13 10:12:16', '2020-11-29 23:45:18');
INSERT INTO `edu_teacher` VALUES ('1327502272851943425', '吴珍发', '计算机辅助人机仿真、计算机图形学、人工智能与物联网', '北京航空航天大学硕士', 2, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/11/29/9c5e7e23db5149358a4b8bf5f22ab40cfile.png', 2, 0, '2020-11-14 14:43:00', '2020-11-29 23:38:44');
INSERT INTO `edu_teacher` VALUES ('1327503263395553282', '陈建辉', '计算机应用', '厦门大学硕士', 2, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/11/29/ebebaf0218e74a26b7022e392c29c421file.png', 5, 0, '2020-11-14 14:46:56', '2020-11-29 23:40:33');
INSERT INTO `edu_teacher` VALUES ('1327503540471275522', '许雪晶', '数据挖掘', '香港大学硕士', 1, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/10/19/6d6087250a2d409ca9c9d25a874853eeIMG_20200225_212932 (3).jpg', 6, 1, '2020-11-14 14:48:03', '2020-11-14 14:48:03');
INSERT INTO `edu_teacher` VALUES ('1327503749863514113', '车艳', '数据处理与数据分析', '东华大学博士学位', 2, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/10/19/6d6087250a2d409ca9c9d25a874853eeIMG_20200225_212932 (3).jpg', 7, 1, '2020-11-14 14:48:52', '2020-11-14 14:48:52');
INSERT INTO `edu_teacher` VALUES ('1327523265280548866', '林鸿熙', '现任莆田学院商学院院长、三级岗教授、华侨大学硕士生导师、福建农林大学硕士生导师、福建师范大学硕士生导师、莆田学院电子商务产业学院常务副院长、中共莆田学院纪委委员，获得福建省第七届高等教育教学成果一等奖（排名第一，2014年），入选福建省高等学校新世纪优秀人才支持项目（2014年），入选福建省本科高校优秀学科带头人海外高端访问学者项目（2015年），入选莆田市优秀人才（壶兰英才，2017年）。主要社会兼职有莆田市经济发展研究中心副主任、中国银监会特聘科技专家、莆田市科协委员等职。', '福建师范大学数学理学学士、重庆大学工商管理硕士、新加坡南洋理工大学访问学者，闽江科学传播学者、莆田市文化名家', 2, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/11/14/55c8b4dd4e534c47b5cfed5e59636b49file.png', 8, 0, '2020-11-14 16:06:25', '2020-11-14 16:06:25');
INSERT INTO `edu_teacher` VALUES ('1327525134816059393', '梁丽珍', '主要授课情况：高级财务会计、财务报表分析、会计综合模拟、个人理财', '厦门大学博士学位', 2, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/11/14/9d3764b930494ae3a7f4a6cbf6a7cb7dfile.png', 9, 0, '2020-11-14 16:13:51', '2020-11-14 16:13:51');
INSERT INTO `edu_teacher` VALUES ('1327526074767974401', '林荣华', '曾指导、指挥中央军委政治工作部歌舞团（原总政歌舞团）合唱团、白俄罗斯国家广播合唱团、白俄罗斯国立音乐学院合唱团等国内外一流团队举办音乐会。\n\n', '莆田学院音乐学院教授、院长、研究生导师，系中国音乐家协会会员、中国合唱协会理事、福建省合唱协会副会长，福建省音乐家协会常务理事、莆田市音乐家协会主席。', 2, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/11/14/4f9b8f1298dd47d880f296bac26aeab4file.png', 4, 0, '2020-11-14 16:17:35', '2020-11-14 16:27:08');
INSERT INTO `edu_teacher` VALUES ('1327526871023034369', '宋一然', '福建省激光精密加工工程技术研究中心主任。主要从事激光加工技术应用、工业机器人研究。已主持完成福建省高校产学合作科技重大项目“平板显示屏玻璃薄板超快激光精细加工关键技术研发及应用”1项、市厅级科研课题5项；参加过国家“九五”科技攻关计划“新型自动植入内窥镜系统研究”和国家“863”计划“全方向蠕动机器人驱动内窥镜系统研究”等科研项目的研究，科研成果通过国家教育部鉴定；主持巨型工程子午轮胎机械设备调试及技术改进、WN150TE/5M全自动五色伺服多功能移印机的研制等横向课题4项。目前已发表论文40多篇，专著、教材等6部，共有5篇论文被SCI、EI检索；担任《莆田学院学报》主编，曾多届担任莆田市非公企业工程系列职务评审委员会主任，多次赴日本东京等大学进行学术交流；承担的《制鞋企业管理系统》、《管道检测机器人的路径规划专家系统的研究》等科研成果为莆田市经济发展做出了积极的贡献，于2006、2008年被中共莆田市委、市政府授予“莆田市拔尖人才”荣誉称号，并被莆田市人民政府授予“科普先进工作者”称号。', '硕士生导师、莆田学院副校长、莆田市拔尖人才、福建省高校教学名师、省级重点学科机械工程学科带头人', 2, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/11/29/f95da89c05cf4710a36ff6fe85e926defile.png', 10, 0, '2020-11-14 16:20:45', '2020-11-29 23:41:51');
INSERT INTO `edu_teacher` VALUES ('1327527910447063042', '张炎生', '在研的主要科研项目 \n1. 美国密执安州自然科学基金 ：密西根大湖Burn港港域波浪震荡之研究（1990.01 – \n   1992.12），主持。 \n2. 台湾省自然科学基金 : 台湾苏澳港港池震荡消坡改善之研究 (1994.1 - 1996.12)，主\n   持。 \n3. 台湾省自然科学基金 ：大气海浪波谱预报系统之研究(1995.1-1998.12)，参与主持。\n4. 中国石油公司能源研究经费 ：高雄港第19至21号＆中油专用码头塌陷原因项目研究调\n查评估.(1995.10-1996.10)，主持。\n5. 中国石油公司能源研究经费 ：基隆深澳港中油公司专用港防波堤及码头改善方案之研究\n   (2001.1-2001.12)，主持。\n6.中国百脑汇公司开发经费 : 山东省德州百脑汇科技广场深基坑地下水开挖施工方案之研\n  究 (2012.1-2012.12)，主持。\n', '美国南加州大学(USC)  硕士、美国普渡大学(Purdue University)  博士', 2, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/11/14/27e4ed439c004c2e9a3c38db48cf755afile.png', 11, 0, '2020-11-14 16:24:53', '2020-11-14 16:24:53');
INSERT INTO `edu_teacher` VALUES ('1327608176653963265', '黄黎红', '主要从事半导体技术与混合图像处理的教学及研究工作，已主持或参与省、市厅级科研项目8项；以独立作者正式发表的论文30多篇，其中：权威刊物9篇，EI收录4篇；出版专著1部，教材1部；获得国家实用新型专利2项，省部级奖项4项，校级10项。', '机电工程学院基础部主任、华侨大学兼职硕士生导师、莆田市政协委员', 2, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/11/14/4a5041b203c24d1f907daaf16b1acd89file.png', 9, 0, '2020-11-14 21:43:50', '2020-11-14 21:44:04');
INSERT INTO `edu_teacher` VALUES ('1327621833353924610', '邹金安', '1984年8月分配到解放军总后勤部后勤科学研究所工作，任助理工程师、参谋；1987年12月转业到莆田市国家安全局工作，任工程师、局机要秘书，团支部书记；1993年12月至今在莆田学院电子信息工程系工作，教授，计算机学科带头人，校教学名师。担任计算机专业教研室主任、系党委委员兼第一党支部书记。主持省、市级多项科研项目， 发表科研论文二十多篇，获得过军队科技进步奖一次。', '毕业于国防科技大学计算机系', 2, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/11/14/139dc85e86954511aa1b128278724c94file.png', 10, 0, '2020-11-14 22:38:06', '2020-11-14 22:38:06');
INSERT INTO `edu_teacher` VALUES ('1333072355345473538', '黄少武', '', '', 1, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/11/29/55ce7f5c73614d35a68c862fa65a915efile.png', 12, 0, '2020-11-29 23:36:31', '2020-11-29 23:36:31');
INSERT INTO `edu_teacher` VALUES ('1333077115414945793', '李少芳', '数学地质专业', '毕业于长春地质学院', 2, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/11/29/99bea2c247874ab78c382cacc97a66fffile.png', 14, 0, '2020-11-29 23:55:26', '2020-11-29 23:55:50');
INSERT INTO `edu_teacher` VALUES ('1333078127009767425', ' 陈梅香', '主要科研项目:\n\n(1) 主持福建省自然科学基金（2018J0142）：广义二次矩阵多项式线性组合的秩不变性研究，2018/04-2021/04\n\n(2)主持省教育厅中青年教师教育科研项目（A类）（JAT170499）：广义二次线性算子的Fredholm性质研究，2017/05—2019/05\n\n(3) 主持福建省自然科学基金（2015J01590）：范畴的极限与极限的范畴及其应用研究，2015/03-2016/06\n\n(4) 主持省教育厅重点项目（2016福建省杰出青年科研培养计划）：广义二次矩阵线性组合的秩不变性，2016/05-2019/05\n\n(5) 主持省教育厅中青年教师教育科研项目（A类）（JA14277）：极限范畴的性质保持与K群研究，2014/05—2016/05\n\n(6)主持省教育厅A类科技项目（JA12286）：广义二次矩阵线性组合的不变性的研究，2012/05—2014/05\n\n\n\n\n\n', '福建师范大学硕士生导师；美国《数学评论》评论员；福建省教育学会数学教学委员会第九届理事', 1, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/11/29/b85d208e1f254a77b89c3528b5a2f815file.png', 16, 0, '2020-11-29 23:59:28', '2020-11-29 23:59:28');
INSERT INTO `edu_teacher` VALUES ('1333079112411160578', '杨剑炉', '', '毕业于福州大学计算机专业', 2, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/11/30/70fe63323de84ceeafd4badd845efecefile.png', 16, 0, '2020-11-30 00:03:22', '2020-11-30 00:03:22');
INSERT INTO `edu_teacher` VALUES ('1333081412630388737', '吴敏敏', '', '毕业于华侨大学', 2, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/11/30/5b08df094a9546b2b26fdf8e727bc0e7file.png', 20, 0, '2020-11-30 00:12:31', '2020-11-30 00:12:31');
INSERT INTO `edu_teacher` VALUES ('1333085455188664321', '陈青青', '', '毕业于厦门大学计算机系', 1, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/11/30/86581d78e105458aa9ecc34653cbef03file.png', 21, 0, '2020-11-30 00:28:35', '2020-11-30 00:28:35');
INSERT INTO `edu_teacher` VALUES ('1333087790942035969', '易亚星', '', '毕业于第二炮兵工程学院', 2, 'https://edu-hugh.oss-cn-shenzhen.aliyuncs.com/2020/11/30/b08da835567f4acda239656fb9ea3476file.png', 22, 0, '2020-11-30 00:37:52', '2020-11-30 00:37:52');

-- ----------------------------
-- Table structure for edu_user
-- ----------------------------
DROP TABLE IF EXISTS `edu_user`;
CREATE TABLE `edu_user`  (
  `id` char(19) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '用户id',
  `email` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '用户邮箱',
  `openid` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '微信openid',
  `mobile` varchar(11) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '手机号',
  `password` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '密码',
  `nickname` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '昵称',
  `sex` tinyint(2) UNSIGNED NULL DEFAULT NULL COMMENT '性别 1 女，2 男',
  `age` tinyint(3) UNSIGNED NULL DEFAULT NULL COMMENT '年龄',
  `avatar` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '用户头像',
  `sign` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '用户签名',
  `is_disabled` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否禁用 1（true）已禁用，  0（false）未禁用',
  `is_deleted` tinyint(1) NOT NULL DEFAULT 0 COMMENT '逻辑删除 1（true）已删除， 0（false）未删除',
  `gmt_create` datetime(0) NOT NULL COMMENT '创建时间',
  `gmt_modified` datetime(0) NOT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '用户表' ROW_FORMAT = Dynamic;

